// TherapistDashboardHome.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import axios from 'axios';
import '../styles/therapistDashboard.css';

function TherapistDashboard() {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [inviteEmail, setInviteEmail] = useState('');

  const handleInviteSubmit = async (e) => {
    e.preventDefault();
    // Llamada al endpoint para generar la invitación:
    try {
      const response = await fetch('http://localhost:5000/api/invitations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ email: inviteEmail })
      });
      const data = await response.json();
      if (!response.ok) {
        alert(data.msg || 'Error al generar la invitación');
      } else {
        alert(`Invitación enviada a ${inviteEmail}`);
        setShowInviteModal(false);
        setInviteEmail('');
      }
    } catch (err) {
      console.error(err);
      alert('Error en el servidor al generar la invitación');
    }
  };

  return (
    <div className="therapist-home-container">
      <header className="home-header">
        <h1>Panel del Terapeuta</h1>
      </header>
      <main className="home-main">
        <section className="management-tools">
          <h2>HERRAMIENTAS DE GESTIÓN</h2>
          <div className="button-grid">
            <button onClick={() => navigate('/therapist/patients')}>
              <i className="fa fa-users" aria-hidden="true"></i>
              <span>Lista de Pacientes</span>
            </button>
            <button onClick={() => navigate('/messaging')}>
              <i className="fa fa-comments" aria-hidden="true"></i>
              <span>Mensajería</span>
            </button>
            <button onClick={() => setShowInviteModal(true)}>
              <i className="fa fa-envelope" aria-hidden="true"></i>
              <span>Generar Invitación</span>
            </button>
          </div>
        </section>
        <section className="statistical-info">
          <h2>INFORMACIÓN ESTADÍSTICA</h2>
          <div className="button-grid">
            <button onClick={() => navigate('/therapist/routines')}>
              <i className="fa fa-clock-o" aria-hidden="true"></i>
              <span>Rutinas y Tiempo</span>
            </button>
            <button onClick={() => navigate('/therapist/interests')}>
              <i className="fa fa-heart" aria-hidden="true"></i>
              <span>Intereses</span>
            </button>
            <button onClick={() => navigate('/therapist/satisfaction')}>
              <i className="fa fa-smile-o" aria-hidden="true"></i>
              <span>Satisfacción</span>
            </button>
          </div>
        </section>
        <section className="calendar-section">
          <h2>Calendario</h2>
          <Calendar onChange={setSelectedDate} value={selectedDate} />
        </section>
      </main>

      {showInviteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Generar Invitación</h3>
            <form onSubmit={handleInviteSubmit}>
              <label htmlFor="invite-email">Correo electrónico del paciente:</label>
              <input
                type="email"
                id="invite-email"
                placeholder="correo@ejemplo.com"
                value={inviteEmail}
                onChange={(e) => setInviteEmail(e.target.value)}
                required
              />
              <div className="modal-buttons">
                <button type="button" onClick={() => setShowInviteModal(false)}>
                  Cancelar
                </button>
                <button type="submit">Enviar Invitación</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default TherapistDashboard;
