// src/components/PatientTemplates.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/routineTemplates.css';

function parseJwt(token) {
  try {
    const [, payload] = token.split('.');
    const b64 = payload.replace(/-/g, '+').replace(/_/g, '/');
    const json = atob(b64);
    return JSON.parse(json);
  } catch (err) {
    console.error('parseJwt error:', err);
    return null;
  }
}

export default function PatientTemplates() {
  const [instances, setInstances] = useState([]);
  const [filterTag, setFilterTag] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInstances = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      // Emulamos TherapistInterests: recuperamos datos sin validaciones extra
      const decoded = parseJwt(token);
      const patientId = decoded?.id;
      if (!patientId) {
        setError('No se pudo identificar al paciente.');
        setLoading(false);
        return;
      }

      try {
        // Llamada igual que TherapistInterests, pero a tu endpoint de instancias
        const res = await axios.get(
          `/api/routines/instances/${patientId}`,
          { headers: { 'x-auth-token': token } }
        );
        // Aseguramos siempre un array
        setInstances(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.error('Error al cargar instancias:', err);
        setError(err.response?.data?.msg || 'Error al cargar plantillas');
      } finally {
        setLoading(false);
      }
    };

    fetchInstances();
  }, []);

  const toggleActivity = async (instanceId, idx) => {
    const token = localStorage.getItem('token');
    try {
      // Igual que TherapistInterests usa axios con x-auth-token
      await axios.post(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        // body { completed } según tu controlador
        { completed: !instances
            .find(i => i._id === instanceId)
            .completedActivities?.includes(idx)
        },
        { headers: { 'x-auth-token': token } }
      );

      // Actualizamos estado localmente
      setInstances(prev =>
        prev.map(inst => {
          if (inst._id !== instanceId) return inst;
          const set = new Set(inst.completedActivities || []);
          if (set.has(idx)) set.delete(idx);
          else set.add(idx);
          return { ...inst, completedActivities: Array.from(set) };
        })
      );
    } catch (err) {
      console.error('Error marcando actividad:', err);
      setError('No se pudo actualizar la actividad.');
    }
  };

  if (loading) return <p>Cargando plantillas asignadas…</p>;
  if (error)   return <p className="error-msg">{error}</p>;

  // Filtrado idéntico al anterior
  const filtered = instances.filter(inst =>
    filterTag
      ? inst.template.tags?.includes(filterTag)
      : true
  );

  return (
    <div className="routine-templates-page patient-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas Asignadas</h1>
          <input
            type="text"
            placeholder="Filtrar por etiqueta"
            value={filterTag}
            onChange={e => setFilterTag(e.target.value)}
            className="small-input tag-filter"
          />
        </div>

        {!filtered.length ? (
          <p>No se encontraron plantillas con esa etiqueta.</p>
        ) : (
          <div className="tpl-grid">
            {filtered.map(inst => (
              <div key={inst._id} className="tpl-card">
                <h2 className="tpl-title">{inst.template.name}</h2>
                <p className="tpl-desc">{inst.template.description}</p>
                <div className="tpl-meta">
                  Duración: {inst.template.duration} min | Tags: {inst.template.tags?.join(', ')}
                </div>

                <ul className="tpl-activities-list">
                  {inst.template.activities.map((act, idx) => {
                    const done = inst.completedActivities?.includes(idx);
                    return (
                      <li key={idx} className="activity-item">
                        <label>
                          <input
                            type="checkbox"
                            checked={done}
                            onChange={() => toggleActivity(inst._id, idx)}
                          />
                          <span className={done ? 'completed' : ''}>
                            {act.name} – Nivel {act.challenge} – {act.minutes} min
                          </span>
                        </label>
                        {act.desc && <p className="activity-desc">{act.desc}</p>}
                      </li>
                    );
                  })}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
