import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/patientsList.css';

function PatientsList() {
  // Estados para gestionar la lista de pacientes y la carga
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // Estados para el modal y el paciente seleccionado
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  
  // Estados para los resultados de los juegos
  const [timeMachineResult, setTimeMachineResult] = useState(null);
  const [planetMapResult, setPlanetMapResult] = useState(null);
  
  // Para controlar la visualización de los detalles de la sesión de Máquina del Tiempo
  const [showTimeMachineDetails, setShowTimeMachineDetails] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/therapist/patients', {
          headers: { 'x-auth-token': token }
        });
        setPatients(response.data);
      } catch (error) {
        console.error(
          'Error al obtener pacientes:',
          error.response ? error.response.data : error.message
        );
      } finally {
        setLoading(false);
      }
    };
    fetchPatients();
  }, []);

  // Consulta el resultado de la Máquina del Tiempo para el paciente (endpoint GET /api/game)
  const fetchTimeMachineResult = async (patientId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/game?patientId=${patientId}`, {
        headers: { 'x-auth-token': token }
      });
      if (response.data && response.data.length > 0) {
        // Se toma el primer resultado o el más reciente
        setTimeMachineResult(response.data[0]);
      } else {
        setTimeMachineResult(null);
      }
    } catch (error) {
      console.error("Error al obtener el resultado de la Máquina del Tiempo:", error);
    }
  };

  // Consulta el registro de Mi Planeta para el paciente (endpoint GET /api/planetMap)
  const fetchPlanetMapResult = async (patientId) => {
    try {
      const token = localStorage.getItem('token');
      // Se asume que el endpoint acepta el parámetro patientId para buscar el registro
      const response = await axios.get(`http://localhost:5000/api/planetMap?patientId=${patientId}`, {
        headers: { 'x-auth-token': token }
      });
      if (response.data) {
        setPlanetMapResult(response.data);
      } else {
        setPlanetMapResult(null);
      }
    } catch (error) {
      console.error("Error al obtener el resultado de Mi Planeta:", error);
    }
  };

  // Al abrir el modal se selecciona el paciente y se obtienen ambos resultados de juego
  const openModal = (patient) => {
    setSelectedPatient(patient);
    setModalOpen(true);
    // Reiniciamos resultados previos
    setTimeMachineResult(null);
    setPlanetMapResult(null);
    setShowTimeMachineDetails(false);
    fetchTimeMachineResult(patient._id);
    fetchPlanetMapResult(patient._id);
  };

  const closeModal = () => {
    setModalOpen(false);
    setSelectedPatient(null);
    setTimeMachineResult(null);
    setPlanetMapResult(null);
    setShowTimeMachineDetails(false);
  };

  // Función para navegar a la sesión completa de algún juego (si se requiere)
  const handleGameSession = (game, patientId) => {
    navigate(`/game-session/${game}?patientId=${patientId}`);
  };

  return (
    <div className="therapist-patient-list">
      <h1>Listado de Pacientes</h1>
      {loading ? (
        <p>Cargando pacientes...</p>
      ) : (
        <table className="patient-table">
          <thead>
            <tr>
              <th>Nombre Completo</th>
              <th>Fecha de Nacimiento</th>
              <th>Email</th>
              <th className="action-col">Acción</th>
            </tr>
          </thead>
          <tbody>
            {patients.map((patient) => (
              <tr key={patient._id}>
                <td>{patient.nombre} {patient.apellidos}</td>
                <td>{new Date(patient.fechaNacimiento).toLocaleDateString()}</td>
                <td>{patient.email}</td>
                <td className="action-col">
                  <button className="detail-btn" onClick={() => openModal(patient)}>
                    <i className="fa fa-search"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {modalOpen && selectedPatient && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Perfil del Paciente</h2>
            <div className="profile-info">
              <p>
                <strong>Nombre:</strong> {selectedPatient.nombre} {selectedPatient.apellidos}
              </p>
              <p>
                <strong>Fecha de Nacimiento:</strong> {new Date(selectedPatient.fechaNacimiento).toLocaleDateString()}
              </p>
              <p>
                <strong>Email:</strong> {selectedPatient.email}
              </p>
            </div>
            <div className="games-info">
              <h3>Estado de Juegos</h3>
              <ul>
                <li>
                  <span>Máquina del Tiempo:</span> {timeMachineResult ? "Completado" : "Pendiente"}
                  <button
                    className="game-detail-btn"
                    onClick={() => setShowTimeMachineDetails(!showTimeMachineDetails)}
                    title={timeMachineResult ? "Ver sesión de la Máquina del Tiempo" : "Juego pendiente"}
                    disabled={!timeMachineResult}
                  >
                    <i className="fa fa-clock-o"></i>
                  </button>
                </li>
                <li>
                  <span>Diario de Emociones:</span> {selectedPatient.emotionsDiaryCompleted ? "Completado" : "Pendiente"}
                  <button
                    className="game-detail-btn"
                    onClick={() => handleGameSession("emotions-diary", selectedPatient._id)}
                    title={selectedPatient.emotionsDiaryCompleted ? "Ver Diario de Emociones" : "Juego pendiente"}
                    disabled={!selectedPatient.emotionsDiaryCompleted}
                  >
                    <i className="fa fa-book"></i>
                  </button>
                </li>
                <li>
                  <span>Mi Planeta:</span> {planetMapResult ? "Completado" : "Pendiente"}
                  <button
                    className="game-detail-btn"
                    onClick={() => handleGameSession("planet-map", selectedPatient._id)}
                    title={planetMapResult ? "Ver Mi Planeta" : "Juego pendiente"}
                    disabled={!planetMapResult}
                  >
                    <i className="fa fa-globe"></i>
                  </button>
                </li>
              </ul>
              {showTimeMachineDetails && timeMachineResult && (
                <div className="time-machine-session">
                  <h4>Detalles de la sesión:</h4>
                  {timeMachineResult.timeSlots.map((slot) => (
                    <div key={slot.slot}>
                      <strong>{slot.slot}</strong>:{" "}
                      {slot.activities.map((activity) => (
                        <span key={activity.activityId}>
                          {activity.title} ({activity.duration} min)&nbsp;
                        </span>
                      ))}
                    </div>
                  ))}
                </div>
              )}
            </div>
            <button className="close-modal-btn" onClick={closeModal}>
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default PatientsList;
