// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaTrash,
  FaEdit,
  FaSearch,
  FaTimes,
  FaCalendar,
  FaClock,
  FaStar,
  FaSync,
  FaFlag,
  FaCheckCircle
} from 'react-icons/fa';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates]         = useState([]);
  const [patients, setPatients]           = useState([]);
  const [loading, setLoading]             = useState(true);
  const [error, setError]                 = useState(null);
  const [showModal, setShowModal]         = useState(false);
  const [modalPatient, setModalPatient]   = useState(null);
  const [modalInstances, setModalInstances] = useState([]);
  const navigate = useNavigate();
  const token    = localStorage.getItem('token');

  // Fetch templates & patients
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    Promise.all([
      axios.get('/api/routines/templates', { headers }),
      axios.get('/api/therapist/patients',   { headers })
    ])
    .then(([tplRes, patRes]) => {
      setTemplates( Array.isArray(tplRes.data) ? tplRes.data : [] );
      setPatients(  Array.isArray(patRes.data) ? patRes.data : [] );
    })
    .catch(err => {
      console.error(err);
      setError(err.message);
    })
    .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag-n-drop handlers
  const onDragStart = (e, item) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type: 'template', item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };
  const allowDrop = e => e.preventDefault();
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      axios.delete(`/api/routines/templates/${item._id}`, { headers }).catch(console.error);
    }
    else if (zone === 'duplicate' && type === 'template') {
      const copy = {
        ...item,
        name: item.name + ' (copia)',
        activities: item.activities.map(a => ({
          name: a.name, desc: a.desc, challenge: a.challenge, minutes: a.minutes
        }))
      };
      axios.post('/api/routines/templates', copy, { headers })
        .then(res => setTemplates(ts => [res.data, ...ts]))
        .catch(err => alert('Error duplicando: ' + err.message));
    }
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      axios.post('/api/routines/instances',
        { templateId: item._id, patientId },
        { headers }
      )
      .then(() => alert(`Plantilla «${item.name}» asignada a paciente ${patientId}`))
      .catch(err => alert('Error asignando: ' + err.message));
    }
  }, [token]);

  // Open patient-detail modal
  const openModal = async patient => {
    setModalPatient(patient);
    setShowModal(true);
    try {
      const res = await axios.get(
        `/api/routines/instances/${patient._id}`,
        { headers: { 'x-auth-token': token } }
      );
      setModalInstances(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error(err);
      setModalInstances([]);
    }
  };
  const closeModal = () => {
    setShowModal(false);
    setModalPatient(null);
    setModalInstances([]);
  };

  if (loading) return <div className="tpl-container">Cargando…</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        {/* Header */}
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            📄 Nueva plantilla
          </button>
        </div>

        {/* Plantillas grid */}
        {templates.length === 0
          ? <div className="empty-state">🤔 Aún no se han creado plantillas</div>
          : <div className="tpl-grid">
              {templates.map(t => (
                <div
                  key={t._id}
                  className="tpl-card"
                  draggable
                  onDragStart={e => onDragStart(e, t)}
                >
                  <FaEdit
                    className="icon-edit"
                    onClick={() => navigate(`/therapist/templates/${t._id}/edit`)}
                  />
                  <h2>{t.name}</h2>
                  <p className="tpl-desc">{t.description}</p>
                  <div className="tpl-meta">{t.activities.length} actividades</div>
                </div>
              ))}
            </div>
        }

        {/* Dropzones */}
        <div className="dropzones">
          <div className="dropzone trash"     onDragOver={allowDrop} onDrop={e => handleDrop(e, 'trash')}>
            🗑️ Eliminar plantilla
          </div>
          <div className="dropzone duplicate" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'duplicate')}>
            📋 Duplicar plantilla
          </div>
        </div>

        {/* Pacientes grid */}
        <h3>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div
              key={p._id}
              className="patient-card"
              onClick={() => openModal(p)}
              onDragOver={allowDrop}
              onDrop={e => handleDrop(e, `assign-${p._id}`)}
            >
              <img src={p.avatar} alt={p.name} className="avatar-sm" />
              <div>
                <strong>{p.name}</strong>
                <div className="text-muted" style={{ fontSize: '0.8rem' }}>{p.email}</div>
              </div>
              <FaSearch className="icon-search" title="Ver estado" />
            </div>
          ))}
        </div>
      </div>

      {/* Modal detalle */}
      {showModal && modalPatient && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <header className="modal-header">
              <h2>Estado de {modalPatient.name}</h2>
              <button className="modal-close" onClick={closeModal}><FaTimes/></button>
            </header>
            <div className="modal-body">
              {modalInstances.length === 0
                ? <p>No hay plantillas asignadas.</p>
                : modalInstances.map(inst => {
                    const tpl   = inst.template;
                    const total = tpl.activities.length;
                    const done  = inst.completedActivities.length;
                    const pct   = Math.round((done/total)*100);
                    return (
                      <div key={inst._id} className="modal-card">
                        <h3>{tpl.name}</h3>
                        <div className="progress-bar">
                          <div className="progress-filled" style={{ width: `${pct}%` }} />
                        </div>
                        <p className="progress-text">{done}/{total} ({pct}%)</p>
                        <div className="tags-container">
                          {tpl.tags?.map(tag => <span key={tag} className="tag-badge">{tag}</span>)}
                        </div>
                        <div className="detail-meta">
                          <div><FaCalendar/> Asignado: {new Date(inst.createdAt).toLocaleDateString()}</div>
                          <div><FaSync/> Última actualización: {new Date(inst.updatedAt||inst.createdAt).toLocaleString()}</div>
                          <div><FaClock/> Duración: {tpl.duration} min</div>
                          <div><FaFlag/> Actividades: {total}</div>
                        </div>
                        <ul className="activities-detailed-list">
                          {tpl.activities.map((act,i) => {
                            const completed = inst.completedActivities.includes(i);
                            return (
                              <li key={i} className={`activity-detail-item ${completed?'completed':''}`}>
                                <span className="activity-icon">
                                  {completed ? <FaCheckCircle/> : <FaTimes/>}
                                </span>
                                <span className="activity-name">{act.name}</span>
                                <div className="activity-info-icons">
                                  <span title="Duración"><FaClock/> {act.minutes} min</span>
                                  <span title="Nivel"><FaStar/> {act.challenge}</span>
                                </div>
                                {act.desc && <p className="activity-desc">{act.desc}</p>}
                              </li>
                            );
                          })}
                        </ul>
                      </div>
                    );
                  })
              }
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
