import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/miPlaneta.css';

const initialElements = [
  { id: 'estadio', title: 'Estadio', icon: 'üèüÔ∏è' },
  { id: 'parque', title: 'Parque', icon: 'üèûÔ∏è' },
  { id: 'colegio', title: 'Colegio', icon: 'üè´' },
  { id: 'museo', title: 'Museo', icon: 'üèõÔ∏è' },
  { id: 'teatro', title: 'Teatro', icon: 'üé≠' },
  { id: 'centro_comercial', title: 'Centro Comercial', icon: 'üè¨' },
  { id: 'biblioteca', title: 'Biblioteca', icon: 'üìö' }
];

const sizes = ['small', 'medium', 'large'];

const generatePlanetZones = () => {
  // Para este ejemplo, definimos zonas preestablecidas (por ejemplo, 4 regiones)
  return [
    { id: 'norte', label: 'Norte', placements: [] },
    { id: 'sur', label: 'Sur', placements: [] },
    { id: 'este', label: 'Este', placements: [] },
    { id: 'oeste', label: 'Oeste', placements: [] }
  ];
};

function MiPlaneta() {
  const navigate = useNavigate();
  const [elements] = useState(initialElements);
  const [planetZones, setPlanetZones] = useState(generatePlanetZones());
  const [zoom, setZoom] = useState(1);

  // Verificar token y redirigir si no existe
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/');
  }, [navigate]);

  const handleZoomChange = (e) => {
    setZoom(parseFloat(e.target.value));
  };

  const onDragEnd = (result) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;
    // Si el elemento viene del pool, creamos una nueva instancia
    if (source.droppableId === 'pool' && destination.droppableId !== 'pool') {
      // extraer id original
      const originalId = draggableId.replace('pool-', '');
      const element = elements.find(el => el.id === originalId);
      if (!element) return;
      // Se crea una instancia con tama√±o por defecto (medium)
      const instance = {
        ...element,
        instanceId: `${element.id}-${Date.now()}`,
        size: 'medium',
        // Se puede guardar la zona destino como posici√≥n (en este ejemplo, el id de la zona)
        zone: destination.droppableId
      };
      const newZones = planetZones.map(zone => {
        if (zone.id === destination.droppableId) {
          return { ...zone, placements: [...zone.placements, instance] };
        }
        return zone;
      });
      setPlanetZones(newZones);
      return;
    }

    // Movimiento dentro de una zona: reordenar
    if (source.droppableId !== 'pool' && destination.droppableId !== 'pool' && source.droppableId === destination.droppableId) {
      const newZones = planetZones.map(zone => {
        if (zone.id === source.droppableId) {
          const newPlacements = Array.from(zone.placements);
          const [movedItem] = newPlacements.splice(source.index, 1);
          newPlacements.splice(destination.index, 0, movedItem);
          return { ...zone, placements: newPlacements };
        }
        return zone;
      });
      setPlanetZones(newZones);
      return;
    }
    // (Opcional) permitir mover entre zonas si se desea
  };

  // Cambiar tama√±o de un elemento: ciclos entre small, medium y large
  const handleSizeChange = (zoneId, instanceId) => {
    const newZones = planetZones.map(zone => {
      if (zone.id === zoneId) {
        const newPlacements = zone.placements.map(item => {
          if (item.instanceId === instanceId) {
            const currentIndex = sizes.indexOf(item.size);
            const newSize = sizes[(currentIndex + 1) % sizes.length];
            return { ...item, size: newSize };
          }
          return item;
        });
        return { ...zone, placements: newPlacements };
      }
      return zone;
    });
    setPlanetZones(newZones);
  };

  // Guardar configuraci√≥n del planeta en el backend
  const handleSavePlanet = async () => {
    const token = localStorage.getItem('token');
    // Transformamos la informaci√≥n para enviar: cada zona con sus placements
    const payload = {
      zoom,
      zones: planetZones.map(zone => ({
        zone: zone.id,
        placements: zone.placements.map(item => ({
          elementId: item.id,
          title: item.title,
          icon: item.icon,
          size: item.size,
          // Aqu√≠ podr√≠amos enviar posici√≥n (en este ejemplo, la zona es la posici√≥n)
          zone: zone.id
        }))
      }))
    };
    try {
      await axios.post('http://localhost:5000/api/planet', payload, {
        headers: { 'x-auth-token': token }
      });
      alert('¬°Tu planeta ha sido guardado!');
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
      alert('Error al guardar el planeta.');
    }
  };

  return (
    <div className="mi-planeta-container">
      <header className="mp-header">
        <div className="mp-header-left">
          <button className="back-dashboard-btn" onClick={() => navigate('/dashboard')}>‚Üê</button>
          <h1>Mi Planeta</h1>
        </div>
      </header>

      <div className="mp-instructions">
        <p>Este es TU PLANETA, ll√©nalo con las cosas que m√°s te gustan hacer</p>
        <p>
          Cada opci√≥n tiene tres tama√±os diferentes. Elige el tama√±o que mejor represente cu√°nto te gusta: 
          m√°s grande si te encanta, mediano si te gusta, y peque√±o si te gusta menos.
        </p>
      </div>

      <div className="mp-controls">
        <label htmlFor="zoom-range">Zoom del planeta:</label>
        <input
          type="range"
          id="zoom-range"
          min="0.5"
          max="2"
          step="0.1"
          value={zoom}
          onChange={handleZoomChange}
        />
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="mp-content">
          {/* Panel de elementos */}
          <div className="mp-pool">
            <h2>Elementos</h2>
            <Droppable droppableId="pool">
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  {elements.map((el, index) => (
                    <Draggable
                      key={`pool-${el.id}`}
                      draggableId={`pool-${el.id}`}
                      index={index}
                    >
                      {(providedDraggable) => (
                        <div
                          className="mp-element-card"
                          ref={providedDraggable.innerRef}
                          {...providedDraggable.draggableProps}
                          {...providedDraggable.dragHandleProps}
                        >
                          <span className="mp-element-icon">{el.icon}</span>
                          <span className="mp-element-title">{el.title}</span>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>

          {/* Mapa del planeta */}
          <div className="mp-planet-area" style={{ transform: `scale(${zoom})` }}>
            <h2>Tu Planeta</h2>
            <div className="mp-zones">
              {planetZones.map(zone => (
                <div key={zone.id} className="mp-zone">
                  <h3>{zone.id.toUpperCase()}</h3>
                  <Droppable droppableId={zone.id}>
                    {(provided) => (
                      <div
                        className="mp-zone-drop"
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                      >
                        {zone.placements.map((item, index) => (
                          <Draggable key={item.instanceId} draggableId={item.instanceId} index={index}>
                            {(providedDraggable) => (
                              <div
                                className={`mp-placed-element ${item.size}`}
                                ref={providedDraggable.innerRef}
                                {...providedDraggable.draggableProps}
                                {...providedDraggable.dragHandleProps}
                                onDoubleClick={() => handleSizeChange(zone.id, item.instanceId)}
                              >
                                <span className="mp-element-icon">{item.icon}</span>
                                <span className="mp-element-title">{item.title}</span>
                              </div>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    )}
                  </Droppable>
                </div>
              ))}
            </div>
          </div>
        </div>
      </DragDropContext>

      <div className="mp-actions">
        <button className="mp-save-btn" onClick={handleSavePlanet}>Guardar mi planeta</button>
      </div>

      <footer className="mp-footer">
        <p>2025 ¬© Iv√°n Vela Campos</p>
      </footer>
    </div>
  );
}

export default MiPlaneta;
