import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar,
  LineChart, Line,
  ResponsiveContainer
} from 'recharts';
import '../styles/therapistRoutines.css';

const categoryMap = {
  futbol: 'Ocio', dibujos: 'Ocio', comics: 'Ocio',
  tarea: 'Obligaciones', videojuegos: 'Ocio', helado: 'Ocio',
  parque: 'Ocio', banio: 'Autocuidado', dormir: 'Autocuidado',
  musica: 'Ocio', bailar: 'Ocio', amigos: 'Ocio',
  bicicleta: 'Ocio', dibujar: 'Ocio', mascotas: 'Ocio',
  experimentos: 'Obligaciones', cantar: 'Ocio', lego: 'Ocio',
  nadar: 'Autocuidado', computadora: 'Ocio'
};
const groupMap = { Autocuidado: 'Autocuidado', Obligaciones: 'Obligaciones', Ocio: 'Ocio' };
const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#0088FE'];

function PatientRoutines() {
  const { patientId } = useParams();
  const [results, setResults] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selected, setSelected] = useState('');
  const [metrics, setMetrics] = useState(null);

  // 1) Cargar resultados de Máquina del Tiempo
  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`https://localhost:8080/api/game?patientId=${patientId}`, {
      headers: { 'x-auth-token': token }
    }).then(resp => {
      const data = resp.data;
      setResults(data);
      // Filtro inicial últimos 7 días
      const now = new Date(), weekAgo = new Date(now);
      weekAgo.setDate(now.getDate() - 6);
      const f = weekAgo.toISOString().slice(0,10);
      const t = now.toISOString().slice(0,10);
      setFrom(f);
      setTo(t);
    }).catch(console.error);
  }, [patientId]);

  // 2) Aplicar filtro de fecha
  useEffect(() => {
    if (!from || !to) return;
    const fDate = new Date(from);
    const tDate = new Date(to);
    tDate.setHours(23,59,59,999);
    const arr = results.filter(r => {
      const d = new Date(r.createdAt);
      return d >= fDate && d <= tDate;
    });
    setFiltered(arr);
  }, [results, from, to]);

  // 3) Seleccionar primer día disponible al aplicar filtro
  useEffect(() => {
    if (filtered.length > 0) {
      setSelected(filtered[0].createdAt);
    } else {
      setSelected('');
      setMetrics(null);
    }
  }, [filtered]);

  // 4) Recalcular métricas al cambiar el día seleccionado
  useEffect(() => {
    if (!selected) return;
    const r = filtered.find(x => x.createdAt === selected);
    if (!r) return setMetrics(null);
    setMetrics(computeMetrics(r, filtered));
  }, [selected, filtered]);

  function computeMetrics(result, allResults) {
    const slots = result.timeSlots || [];
    const acts = slots.flatMap(s => (s.activities||[]).map(a => ({...a, slot:s.slot})));
    const total = acts.reduce((s,a) => s + a.duration, 0) || 1;

    // Distribución %
    const catSum = {};
    acts.forEach(a => {
      const c = categoryMap[a.activityId] || 'Otros';
      catSum[c] = (catSum[c]||0) + a.duration;
    });
    const distribution = Object.entries(catSum).map(([name,val]) => ({
      name, value: +((val/total)*100).toFixed(1)
    }));

    // Horas absolutas
    const hours = Object.entries(catSum).map(([name,val]) => ({
      name, hours: +(val/60).toFixed(2)
    }));

    // Diversidad
    const diversity = new Set(acts.map(a=>a.activityId)).size;

    // Consistencia diaria
    const covered = slots.filter(s=>s.activities?.length>0).length;
    const consistency = +((covered/24)*100).toFixed(1);

    // Patrón mañana/tarde/noche
    const periods = { Mañana:[6,11], Tarde:[12,17], Noche:[18,23] };
    const pdSum = { Mañana:0, Tarde:0, Noche:0 };
    acts.forEach(a => {
      const h = parseInt(a.slot.split(':')[0],10);
      for (let p in periods) {
        const [s,e] = periods[p];
        if (h>=s && h<=e) pdSum[p] += a.duration;
      }
    });
    const timeOfDay = Object.entries(pdSum).map(([name,val]) => ({
      name, hours: +(val/60).toFixed(2)
    }));

    // Balance de grupos
    const grpSum = { Autocuidado:0, Obligaciones:0, Ocio:0 };
    acts.forEach(a => {
      const cat = categoryMap[a.activityId] || 'Ocio';
      const grp = groupMap[cat] || 'Ocio';
      grpSum[grp] += a.duration;
    });
    const groupBalance = Object.entries(grpSum).map(([subject,val]) => ({
      subject, value: +((val/total)*100).toFixed(1)
    }));

    // Tendencia semanal
    const trend = allResults.map(r => {
      const sl = r.timeSlots || [];
      const ac = sl.flatMap(s => (s.activities||[]).map(a=>({...a,slot:s.slot})));
      const tot = ac.reduce((s,a)=>s+a.duration,0)||1;
      const sums = { Autocuidado:0, Obligaciones:0, Ocio:0 };
      ac.forEach(a => {
        const cat = categoryMap[a.activityId] || 'Ocio';
        const g = groupMap[cat] || 'Ocio';
        sums[g]+=a.duration;
      });
      return {
        date: new Date(r.createdAt).toLocaleDateString(),
        Autocuidado: +((sums.Autocuidado/tot)*100).toFixed(1),
        Obligaciones:+((sums.Obligaciones/tot)*100).toFixed(1),
        Ocio:       +((sums.Ocio/tot)*100).toFixed(1)
      };
    });

    // Índice de equilibrio (Shannon‐simplificado)
    const N = distribution.length;
    const uniform = 1/N;
    const idx = 1 - (distribution.reduce((acc,c)=>acc+Math.abs(c.value-uniform),0)/2);
    const balanceIndex = +idx.toFixed(2);

    return { distribution, hours, diversity, consistency, timeOfDay, groupBalance, trend, balanceIndex };
  }

  return (
    <div className="routines-container">
      <header className="routines-header">
        <Link to="/therapist/routines">← Volver</Link>
        <h2>Estadísticas de Rutinas</h2>
      </header>

      <section className="filter-section">
        <label>Desde:<input type="date" value={from} onChange={e=>setFrom(e.target.value)} /></label>
        <label>Hasta:<input type="date" value={to} onChange={e=>setTo(e.target.value)} /></label>
        <label>Seleccionar día:
          <select value={selected} onChange={e=>setSelected(e.target.value)}>
            {filtered.map(r => {
              const d = new Date(r.createdAt).toLocaleDateString();
              return <option key={r.createdAt} value={r.createdAt}>{d}</option>;
            })}
          </select>
        </label>
      </section>

      {metrics && (
        <>
          <div className="metrics-grid">
            <div className="metric-card">Diversidad: <strong>{metrics.diversity}</strong></div>
            <div className="metric-card">Consistencia: <strong>{metrics.consistency}%</strong></div>
            <div className="metric-card">Índice Equilibrio: <strong>{metrics.balanceIndex}</strong></div>
          </div>

          <div className="charts-grid">
            <div className="chart-block">
              <h3>Distribución % por Categoría</h3>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie data={metrics.distribution} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
                    {metrics.distribution.map((_,i)=><Cell key={i} fill={COLORS[i%COLORS.length]}/>)}
                  </Pie>
                  <Tooltip/>
                  <Legend/>
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Horas por Categoría</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={metrics.hours} margin={{ top:20, right:30, left:0, bottom:0 }}>
                  <CartesianGrid strokeDasharray="3 3"/>
                  <XAxis dataKey="name"/>
                  <YAxis/>
                  <Tooltip/>
                  <Bar dataKey="hours" fill={COLORS[0]}/>
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block">
              <h3>Balance Autocuidado/Obligaciones/Ocio</h3>
              <ResponsiveContainer width="100%" height={250}>
                <RadarChart data={metrics.groupBalance} cx="50%" cy="50%" outerRadius={80}>
                  <PolarGrid/>
                  <PolarAngleAxis dataKey="subject"/>
                  <PolarRadiusAxis domain={[0,100]}/>
                  <Radar dataKey="value" stroke={COLORS[1]} fill={COLORS[1]} fillOpacity={0.6}/>
                  <Tooltip/>
                </RadarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-block full-width">
              <h3>Tendencia Semanal (% por Grupo)</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={metrics.trend} margin={{ top:20, right:30, left:0, bottom:0 }}>
                  <CartesianGrid strokeDasharray="3 3"/>
                  <XAxis dataKey="date"/>
                  <YAxis/>
                  <Tooltip/>
                  <Line type="monotone" dataKey="Autocuidado" stroke={COLORS[0]}/>
                  <Line type="monotone" dataKey="Obligaciones" stroke={COLORS[1]}/>
                  <Line type="monotone" dataKey="Ocio" stroke={COLORS[2]}/>
                  <Legend/>
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default PatientRoutines;
