// src/components/PatientRoutines.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar,
  LineChart, Line,
  ResponsiveContainer
} from 'recharts';
import '../styles/therapistRoutines.css';

// Mapeo de actividad a categoría
const categoryMap = {
  futbol: 'Ocio', dibujos: 'Ocio', comics: 'Ocio',
  tarea: 'Obligaciones', videojuegos: 'Ocio', helado: 'Ocio',
  parque: 'Ocio', banio: 'Autocuidado', dormir: 'Autocuidado',
  musica: 'Ocio', bailar: 'Ocio', amigos: 'Ocio',
  bicicleta: 'Ocio', dibujar: 'Ocio', mascotas: 'Ocio',
  experimentos: 'Obligaciones', cantar: 'Ocio', lego: 'Ocio',
  nadar: 'Autocuidado', computadora: 'Ocio'
};
// Grupos para radar y gauge
const groupMap = {
  Autocuidado: 'Autocuidado',
  Obligaciones: 'Obligaciones',
  Ocio: 'Ocio'
};
// Colores
const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff8042', '#0088FE'];

function PatientRoutines() {
  const { patientId } = useParams();
  const [results, setResults] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selected, setSelected] = useState(null);
  const [metrics, setMetrics] = useState(null);
  
  // Fetch resultados
  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`/api/game?patientId=${patientId}`, {
      headers: { 'x-auth-token': token }
    }).then(resp => {
      setResults(resp.data);
      // filtro inicial últimos 7 días
      const now = new Date(), weekAgo = new Date(now);
      weekAgo.setDate(now.getDate() - 6);
      setFrom(weekAgo.toISOString().slice(0,10));
      setTo(now.toISOString().slice(0,10));
    }).catch(console.error);
  }, [patientId]);

  // Aplicar filtro de fechas
  useEffect(() => {
    if (!from||!to) return;
    const fDate = new Date(from), tDate = new Date(to);
    tDate.setHours(23,59,59,999);
    setFiltered(results.filter(r=>{
      const d = new Date(r.createdAt);
      return d>=fDate && d<=tDate;
    }));
  }, [results, from, to]);

  // Seleccionar primer resultado
  useEffect(() => {
    if (filtered.length) setSelected(filtered[0].createdAt);
    else setSelected(null);
  }, [filtered]);

  // Calcular métricas
  useEffect(() => {
    if (!selected) return;
    const res = filtered.find(r=>r.createdAt===selected);
    setMetrics(res ? computeMetrics(res, filtered) : null);
  }, [selected, filtered]);

  function computeMetrics(result, allResults) {
    const slots = result.timeSlots || [];
    const activities = slots.flatMap(slot =>
      (slot.activities||[]).map(act=>({ ...act, slot: slot.slot }))
    );
    const totalMin = activities.reduce((sum,a)=>sum+a.duration,0);

    // 1. Distribución %
    const catSum = {};
    activities.forEach(a=>{
      const c = categoryMap[a.activityId]||'Otros';
      catSum[c] = (catSum[c]||0) + a.duration;
    });
    const distribution = Object.entries(catSum).map(([name,val])=>({
      name,
      value: +((val/totalMin)*100).toFixed(1)
    }));

    // 2. Horas absolutas
    const hours = Object.entries(catSum).map(([name,val])=>({
      name, hours: +(val/60).toFixed(2)
    }));

    // 3. Diversidad
    const diversity = new Set(activities.map(a=>a.activityId)).size;

    // 4. Consistencia diaria
    const covered = slots.filter(s=>s.activities?.length>0).length;
    const consistency = +((covered/24)*100).toFixed(1);

    // 5. Tiempo por momento del día
    const periods = { Mañana:[6,11], Tarde:[12,17], Noche:[18,23] };
    const pdSum = { Mañana:0, Tarde:0, Noche:0 };
    activities.forEach(a=>{
      const h = parseInt(a.slot.split(':')[0],10);
      for(let p in periods){
        const [s,e]=periods[p];
        if (h>=s&&h<=e) pdSum[p]+=a.duration;
      }
    });
    const timeOfDay = Object.entries(pdSum).map(([name,val])=>({
      name, hours: +(val/60).toFixed(2)
    }));

    // 6. Balance por grupo
    const grpSum = { Autocuidado:0, Obligaciones:0, Ocio:0 };
    activities.forEach(a=>{
      const cat=categoryMap[a.activityId]||'Ocio';
      const grp=groupMap[cat]||'Ocio';
      grpSum[grp]+=a.duration;
    });
    const groupBalance = Object.entries(grpSum).map(([subject,val])=>({
      subject,
      value: +((val/totalMin)*100).toFixed(1)
    }));

    // 7. Tendencia semanal
    const trend = allResults.map(r=>{
      const acts = (r.timeSlots||[]).flatMap(sl=>sl.activities.map(a=>({...a, slot:sl.slot})));
      const total = acts.reduce((s,a)=>s+a.duration,0);
      const sums = { Autocuidado:0, Obligaciones:0, Ocio:0 };
      acts.forEach(a=>{
        const cat=categoryMap[a.activityId]||'Ocio';
        sums[groupMap[cat]||'Ocio']+=a.duration;
      });
      return {
        date: new Date(r.createdAt).toLocaleDateString(),
        Autocuidado:+((sums.Autocuidado/total)*100).toFixed(1),
        Obligaciones:+((sums.Obligaciones/total)*100).toFixed(1),
        Ocio:+((sums.Ocio/total)*100).toFixed(1)
      };
    });

    // 8. Índice equilibrio global
    const N = distribution.length;
    const uniform = 1/N;
    const idx = 1 - (distribution.reduce((acc,cat)=>(acc+Math.abs(cat.value-uniform)),0)/2);
    const balanceIndex = +idx.toFixed(2);

    // 9. Continuidad vs Caos
    const sortedSlots = [...slots].sort((a,b)=>
      parseInt(a.slot,10) - parseInt(b.slot,10)
    );
    const flatActs = sortedSlots.flatMap(sl=>
      (sl.activities||[]).map(a=>({
        category: categoryMap[a.activityId]||'Otros'
      }))
    );
    let cont=0, trans=flatActs.length-1;
    for(let i=0;i<trans;i++){
      if(flatActs[i].category===flatActs[i+1].category) cont++;
    }
    const continuityRatio = trans>0 ? cont/trans : 0;
    const continuityData = [
      { name:'Continuidad', value:+(continuityRatio*100).toFixed(1) },
      { name:'Caos',       value:+((1-continuityRatio)*100).toFixed(1) }
    ];

    // 10. Correspondencia Mié vs Sáb
    const dayGroups = { Miércoles:[], Sábado:[] };
    allResults.forEach(r=>{
      if(dayGroups[r.day]) dayGroups[r.day].push(r);
    });
    function avgDist(arr){
      const sum={}; let tot=0;
      arr.forEach(r=>{
        r.timeSlots.forEach(sl=>
          sl.activities.forEach(a=>{
            const g = groupMap[categoryMap[a.activityId]]||'Ocio';
            sum[g]=(sum[g]||0)+a.duration;
            tot+=a.duration;
          })
        );
      });
      const dist={};
      for(const k in sum) dist[k]=sum[k]/tot;
      return dist;
    }
    const wedDist = avgDist(dayGroups['Miércoles']||[]);
    const satDist = avgDist(dayGroups['Sábado']||[]);
    const cats = Object.keys(groupMap);
    const correspondenceData = cats.map(cat=>({
      category: cat,
      Miércoles: +( (wedDist[cat]||0)*100 ).toFixed(1),
      Sábado:    +( (satDist[cat]||0)*100 ).toFixed(1)
    }));
    let overlap=0;
    cats.forEach(cat=> overlap += Math.min(wedDist[cat]||0, satDist[cat]||0) );
    const correspondenceIndex = +(overlap*100).toFixed(1);

    return {
      distribution, hours, diversity, consistency,
      timeOfDay, groupBalance, trend, balanceIndex,
      continuityData, continuityRatio: +(continuityRatio*100).toFixed(1),
      correspondenceData, correspondenceIndex
    };
  }

  return (
    <div className="routines-container">
      <header className="routines-header">
        <Link to="/therapist/routines">← Volver</Link>
        <h2>Estadísticas de Rutinas</h2>
      </header>

      <section className="filter-section">
        <label>Desde:<input type="date" value={from}
          onChange={e=>setFrom(e.target.value)} /></label>
        <label>Hasta:<input type="date" value={to}
          onChange={e=>setTo(e.target.value)} /></label>
        <label>Seleccionar día:
          <select value={selected||''}
            onChange={e=>setSelected(e.target.value)}>
            {filtered.map(r=>{
              const d = new Date(r.createdAt).toLocaleDateString();
              return <option key={r.createdAt}
                value={r.createdAt}>{d}</option>;
            })}
          </select>
        </label>
      </section>

      {metrics && (
        <div className="metrics-grid">
          <div className="metric-card">
            Diversidad: <strong>{metrics.diversity}</strong>
          </div>
          <div className="metric-card">
            Consistencia: <strong>{metrics.consistency}%</strong>
          </div>
          <div className="metric-card">
            Índice Equilibrio: <strong>{metrics.balanceIndex}</strong>
          </div>
          <div className="metric-card">
            Continuidad: <strong>{metrics.continuityRatio}%</strong>
          </div>
          <div className="metric-card">
            Correspondencia Mié/Sáb: <strong>{metrics.correspondenceIndex}%</strong>
          </div>
        </div>
      )}

      {metrics && (
        <div className="charts-grid">
          {/* Distribución */}
          <div className="chart-block">
            <h3>Distribución % por Categoría</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie data={metrics.distribution} dataKey="value"
                  nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
                  {metrics.distribution.map((e,i)=>
                    <Cell key={i} fill={COLORS[i%COLORS.length]}/>
                  )}
                </Pie>
                <Tooltip />
                <Legend verticalAlign="bottom" />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Horas */}
          <div className="chart-block">
            <h3>Horas por Categoría</h3>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={metrics.hours}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="hours" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          {/* Balance Radar */}
          <div className="chart-block">
            <h3>Balance Autocuidado/Obligaciones/Ocio</h3>
            <ResponsiveContainer width="100%" height={250}>
              <RadarChart data={metrics.groupBalance}
                cx="50%" cy="50%" outerRadius={80}>
                <PolarGrid />
                <PolarAngleAxis dataKey="subject" />
                <PolarRadiusAxis domain={[0,100]}/>
                <Radar dataKey="value" stroke="#82ca9d"
                  fill="#82ca9d" fillOpacity={0.6}/>
                <Tooltip />
              </RadarChart>
            </ResponsiveContainer>
          </div>

          {/* Tendencia */}
          <div className="chart-block full-width">
            <h3>Tendencia Semanal (% por Grupo)</h3>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={metrics.trend}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Line dataKey="Autocuidado" stroke="#8884d8" />
                <Line dataKey="Obligaciones" stroke="#82ca9d" />
                <Line dataKey="Ocio" stroke="#ffc658" />
                <Legend />
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Continuidad vs Caos */}
          <div className="chart-block">
            <h3>Patrón: Continuidad vs Caos</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie data={metrics.continuityData} dataKey="value"
                  nameKey="name" cx="50%" cy="50%" innerRadius={40}
                  outerRadius={80} label>
                  {metrics.continuityData.map((e,i)=>
                    <Cell key={i} fill={COLORS[(i+1)%COLORS.length]}/>
                  )}
                </Pie>
                <Tooltip />
                <Legend verticalAlign="bottom" />
              </PieChart>
            </ResponsiveContainer>
          </div>

          {/* Comparativa Mié vs Sáb */}
          <div className="chart-block full-width">
            <h3>Comparativa Miércoles vs Sábado</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={metrics.correspondenceData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="category" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="Miércoles" fill="#8884d8" />
                <Bar dataKey="Sábado"    fill="#82ca9d" />
                <Legend />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      )}
    </div>
  );
}

export default PatientRoutines;
