// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaTrash, FaEdit } from 'react-icons/fa';
import '../styles/routineTemplates.css';
import '../components/patientListModal.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates]             = useState([]);
  const [patients, setPatients]               = useState([]);
  const [loading, setLoading]                 = useState(true);
  const [error, setError]                     = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState(null);

  // Estados para el modal de detalle
  const [modalOpen, setModalOpen]             = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [instance, setInstance]               = useState(null);
  const [loadingModal, setLoadingModal]       = useState(false);

  const navigate = useNavigate();
  const token    = localStorage.getItem('token');

  // Fetch inicial de plantillas y pacientes
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }

    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    Promise.all([
      fetch('/api/routines/templates', { headers }),
      fetch('/api/therapist/patients',   { headers })
    ])
    .then(async ([resTpl, resPat]) => {
      if (resTpl.status === 401 || resPat.status === 401) {
        alert('Sesión expirada. Vuelve a iniciar sesión.');
        navigate('/login');
        return;
      }
      const dataTpl = await resTpl.json();
      const dataPat = await resPat.json();
      if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
        throw new Error('Respuesta inesperada del servidor');
      }
      setTemplates(dataTpl);
      setPatients(dataPat);
    })
    .catch(err => {
      console.error(err);
      setError(err.message);
    })
    .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag start: guardamos tipo y payload
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData(
      'application/json',
      JSON.stringify({ type, item })
    );
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  // Drop genérico (basado en zona)
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(
      e.dataTransfer.getData('application/json')
    );
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, { method: 'DELETE', headers })
        .catch(console.error);
    }
    // Duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      const payload = {
        name:        item.name + ' (copia)',
        description: item.description,
        category:    item.category,
        tags:        item.tags,
        duration:    item.duration,
        reminder:    item.reminder,
        activities:  item.activities.map(a => ({
          name:      a.name,
          desc:      a.desc,
          challenge: a.challenge,
          minutes:   a.minutes
        }))
      };
      fetch('/api/routines/templates', {
        method: 'POST',
        headers,
        body: JSON.stringify(payload)
      })
      .then(res => {
        if (!res.ok) return res.text().then(text => { throw new Error(text) });
        return res.json();
      })
      .then(newTpl => {
        setTemplates(ts => [newTpl, ...ts]);
      })
      .catch(err => {
        console.error('Error duplicando plantilla:', err);
        alert('No se pudo duplicar: ' + err.message);
      });
    }
    // Asignar a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch('/api/routines/instances', {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id, patientId })
      })
      .then(res => {
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        alert(`Plantilla «${item.name}» asignada a paciente ${patientId}`);
      })
      .catch(err => {
        console.error('Error asignando plantilla:', err);
        alert('No se pudo asignar: ' + err.message);
      });
    }
  }, [token]);

  const allowDrop = e => e.preventDefault();

  // Click sobre un paciente para abrir modal de detalle
  const handleCardClick = async (patient) => {
    if (!selectedTemplate) return; // no hay plantilla seleccionada
    setSelectedPatient(patient);
    setModalOpen(true);
    setLoadingModal(true);

    try {
      // Traer todas las instancias de ese paciente
      const res = await axios.get(
        `/api/routines/instances/${patient._id}`,
        { headers: { 'x-auth-token': token } }
      );
      const list = Array.isArray(res.data)
        ? res.data
        : Array.isArray(res.data.instances)
          ? res.data.instances
          : [];
      // Filtrar por plantilla seleccionada
      const inst = list.find(i => i.template._id === selectedTemplate._id);
      setInstance(inst || null);
    } catch (err) {
      console.error(err);
      setInstance(null);
    } finally {
      setLoadingModal(false);
    }
  };

  const closeModal = () => {
    setModalOpen(false);
    setInstance(null);
    setSelectedPatient(null);
  };

  if (loading) return <div className="tpl-container">Cargando plantillas…</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        {/* Cabecera y creación */}
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button
            className="btn-new"
            onClick={() => navigate('/therapist/templates/new')}
          >
            📄 Nueva plantilla
          </button>
        </div>

        {/* Rejilla de plantillas */}
        {templates.length === 0 ? (
          <div
            className="empty-state"
            style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}
          >
            🤔 Aún no se han creado plantillas
          </div>
        ) : (
          <div className="tpl-grid">
            {templates.map(t => (
              <div
                key={t._id}
                className={`tpl-card${selectedTemplate?._id === t._id ? ' selected' : ''}`}
                draggable
                onClick={() => setSelectedTemplate(t)}
                onDragStart={e => onDragStart(e, t, 'template')}
              >
                <FaEdit
                  className="icon-edit"
                  onClick={e => {
                    e.stopPropagation();
                    navigate(`/therapist/templates/${t._id}/edit`);
                  }}
                />
                <h2>{t.name}</h2>
                <p className="tpl-desc">{t.description}</p>
                <div className="tpl-meta">{t.activities.length} actividades</div>
              </div>
            ))}
          </div>
        )}

        {/* Zonas drag & drop */}
        <div
          className="dropzones"
          style={{ display: 'flex', gap: '1rem', marginTop: '2rem' }}
        >
          <div
            className="dropzone trash"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'trash')}
          >
            🗑️ Arrastra aquí para eliminar
          </div>
          <div
            className="dropzone duplicate"
            onDragOver={allowDrop}
            onDrop={e => handleDrop(e, 'duplicate')}
          >
            📋 Arrastra aquí Ctrl+drag para duplicar
          </div>
        </div>

        {/* Lista de pacientes para asignar y detalle */}
        {selectedTemplate && (
          <>
            <h3 style={{ marginTop: '2rem' }}>Pacientes</h3>
            <div className="tpl-grid">
              {patients.map(p => (
                <div
                  key={p._id}
                  className="patient-card"
                  onClick={() => handleCardClick(p)}
                  onDragOver={allowDrop}
                  onDrop={e => handleDrop(e, `assign-${p._id}`)}
                >
                  <img
                    src={p.avatar}
                    alt={p.name}
                    style={{ width: 40, borderRadius: '50%' }}
                  />
                  <div style={{ marginLeft: '0.5rem' }}>{p.name}</div>
                  <div style={{ fontSize: '0.8rem', color: '#666' }}>
                    {p.email}
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>

      {/* Modal de detalle de actividades por paciente */}
      {modalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <h2>
              {selectedTemplate.name} – {selectedPatient.name}
            </h2>
            {loadingModal ? (
              <p>Cargando estado…</p>
            ) : instance ? (
              <>
                <p>
                  {instance.completedActivities.length} de{' '}
                  {instance.template.activities.length} completadas
                </p>
                <ul className="status-list">
                  {instance.template.activities.map((act, i) => (
                    <li key={i}>
                      <input
                        type="checkbox"
                        checked={instance.completedActivities.includes(i)}
                        disabled
                      />
                      <span
                        className={
                          instance.completedActivities.includes(i)
                            ? 'completed'
                            : ''
                        }
                      >
                        {act.name}
                      </span>
                    </li>
                  ))}
                </ul>
              </>
            ) : (
              <p>El paciente no tiene asignada esta plantilla.</p>
            )}
            <button className="close-modal-btn" onClick={closeModal}>
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
