// src/pages/RoutineTemplatesPage.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates] = useState([]);
  const [patients, setPatients]   = useState([]);
  const navigate = useNavigate();

  // Fetch inicial
  useEffect(() => {
    fetch('/api/routinetemplates')
      .then(r => r.json())
      .then(setTemplates)
      .catch(console.error);
    fetch('/api/patients')
      .then(r => r.json())
      .then(setPatients)
      .catch(console.error);
  }, []);

  // Drag start: guardamos tipo y payload
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    // Soporte duplicado si se mantiene Ctrl
    e.dataTransfer.effectAllowed = 'copyMove';
  };

  // Drop gen√©rico
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));

    if (zone === 'trash' && type === 'template') {
      // Eliminar template
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routinetemplates/${item._id}`, { method: 'DELETE' });
    }
    if (zone === 'duplicate' && type === 'template') {
      // Duplicar template
      const copy = { ...item, name: item.name + ' (copia)', _id: Math.random().toString() };
      setTemplates(ts => [copy, ...ts]);
      // Opcional: POST a backend
      fetch('/api/routinetemplates', {
        method:'POST', headers:{'Content-Type':'application/json'},
        body: JSON.stringify(copy)
      });
    }
    if (zone.startsWith('assign-') && type === 'template') {
      // Asignar a paciente
      const patientId = zone.split('-')[1];
      fetch(`/api/patients/${patientId}/assignTemplate`, {
        method:'POST', headers:{'Content-Type':'application/json'},
        body: JSON.stringify({ templateId: item._id })
      });
      alert(`Plantilla ¬´${item.name}¬ª asignada a paciente ${patientId}`);
    }
  }, []);

  const allowDrop = e => { e.preventDefault(); };

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0
          ? (
            <div className="empty-state" style={{
              border: '2px dashed var(--primary-color)',
              padding: '2rem',
              textAlign: 'center',
              borderRadius: '8px',
              color: 'var(--primary-color)',
              fontSize: '1.2rem',
              marginTop: '2rem'
            }}>
              ü§î A√∫n no se han creado plantillas
            </div>
          )
          : (
            <div className="tpl-grid">
              {templates.map(t => (
                <div key={t._id}
                     className="tpl-card"
                     draggable
                     onDragStart={e => onDragStart(e, t, 'template')}>
                  <h2>{t.name}</h2>
                  <p className="tpl-desc">{t.description}</p>
                  <div className="tpl-meta">
                    {t.activities.length} actividades
                  </div>
                </div>
              ))}
            </div>
          )
        }

        {/* Papelera y duplicado */}
        <div className="dropzones" style={{ display:'flex', gap:'1rem', marginTop:'2rem' }}>
          <div className="dropzone trash"
               onDragOver={allowDrop}
               onDrop={e => handleDrop(e, 'trash')}>
            üóëÔ∏è Arrastra aqu√≠ para eliminar
          </div>
          <div className="dropzone duplicate"
               onDragOver={allowDrop}
               onDrop={e => handleDrop(e, 'duplicate')}>
            üìã Arrastra aqu√≠ Ctrl+drag para duplicar
          </div>
        </div>

        {/* Lista de pacientes para asignar */}
        <h3 style={{ marginTop:'2rem' }}>Pacientes</h3>
        <div className="tpl-grid">
          {patients.map(p => (
            <div key={p._id}
                 className="patient-card"
                 onDragOver={allowDrop}
                 onDrop={e => handleDrop(e, `assign-${p._id}`)}>
              <img src={p.avatar} alt="" style={{ width:40, borderRadius:'50%' }}/>
              <div style={{ marginLeft:'0.5rem' }}>{p.name}</div>
              <div style={{ fontSize:'0.8rem', color:'#666' }}>{p.email}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
