// src/pages/PatientTemplatesPage.js
import React, { Component } from 'react';
import { withRouter } from '../../backend/utils/withRouter';
import '../styles/patientTemplates.css';
import { FaCheckCircle, FaRegCircle, FaFileDownload } from 'react-icons/fa';

class PatientTemplatesPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      templates: [],
      filter: 'All',
      search: '',
      loading: true,
      error: null
    };
  }

  componentDidMount() {
    const token = localStorage.getItem('token');
    let patientId = null;
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      patientId = payload.user.id;
    } catch {
      return this.props.navigate('/login');
    }
    this.fetchTemplates(patientId, token);
  }

  fetchTemplates = async (patientId, token) => {
    try {
      const res = await fetch(`/api/routins/instances/${patientId}`, {
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        }
      });
      if (res.status === 401) throw new Error('Sesión expirada');
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const data = await res.json();
      this.setState({
        templates: data.sort((a, b) => new Date(b.assignedAt) - new Date(a.assignedAt)),
        loading: false
      });
    } catch (error) {
      this.setState({ error: error.message, loading: false });
    }
  };

  toggleActivity = async (instanceId, idx) => {
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        { method: 'POST', headers: { 'Content-Type': 'application/json', 'x-auth-token': token } }
      );
      if (!res.ok) throw new Error(`Error ${res.status}`);
      const updated = await res.json();
      this.setState(({ templates }) => ({
        templates: templates.map(t => (t._id === instanceId ? updated : t))
      }));
    } catch (err) {
      console.error(err);
      alert('No se pudo actualizar el estado de la actividad.');
    }
  };

  handleFilterChange = e => {
    this.setState({ filter: e.target.value });
  };

  handleSearchChange = e => {
    this.setState({ search: e.target.value });
  };

  render() {
    const { templates, filter, search, loading, error } = this.state;
    const { navigate } = this.props;

    if (loading) return <div className="ptpl-container">Cargando…</div>;
    if (error) return <div className="ptpl-container error">Error: {error}</div>;

    const visible = templates.filter(tpl => {
      if (search && !tpl.name.toLowerCase().includes(search.toLowerCase())) {
        return false;
      }
      const doneCount = tpl.activities.filter(a => a.completed).length;
      if (filter === 'Completed' && doneCount !== tpl.activities.length) return false;
      if (filter === 'InProgress' && doneCount === 0) return false;
      return true;
    });

    return (
      <div className="patient-templates-page">
        <div className="ptpl-controls">
          <input
            type="text"
            placeholder="Buscar plantilla..."
            value={search}
            onChange={this.handleSearchChange}
          />
          <select value={filter} onChange={this.handleFilterChange}>
            <option value="All">Todas</option>
            <option value="InProgress">En progreso</option>
            <option value="Completed">Completadas</option>
          </select>
        </div>

        {visible.length === 0 ? (
          <div className="ptpl-empty">No hay plantillas que mostrar</div>
        ) : (
          <div className="ptpl-grid">
            {visible.map(tpl => {
              const doneCount = tpl.activities.filter(a => a.completed).length;
              const totalCount = tpl.activities.length;
              const pct = totalCount > 0 ? Math.round((doneCount / totalCount) * 100) : 0;
              return (
                <div key={tpl._id} className="ptpl-card">
                  <h2>{tpl.name}</h2>
                  <p className="ptpl-desc">{tpl.description}</p>

                  <div className="ptpl-meta">
                    <div className="ptpl-progress-bar">
                      <div className="ptpl-progress" style={{ width: `${pct}%` }} />
                    </div>
                    <span>{pct}% completado</span>
                  </div>

                  <ul className="ptpl-activities">
                    {tpl.activities.map((act, i) => (
                      <li key={i}>
                        <button
                          className="ptpl-toggle"
                          onClick={() => this.toggleActivity(tpl._id, i)}
                        >
                          {act.completed ? <FaCheckCircle /> : <FaRegCircle />}
                        </button>
                        <div className="ptpl-act-info">
                          <strong>{act.name}</strong>
                          <span>{act.desc}</span>
                        </div>
                        <span className="ptpl-time">{act.minutes}′</span>
                      </li>
                    ))}
                  </ul>

                  <div className="ptpl-footer">
                    <button
                      className="btn-download"
                      onClick={() => alert('Descargando informe PDF…')}
                    >
                      <FaFileDownload /> Informe PDF
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  }
}

export default withRouter(PatientTemplatesPage);
