import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/home.css';  // Hoja de estilos

function Home() {
  const navigate = useNavigate();

  // Función para cerrar sesión (redirige al login)
  const handleLogout = () => {
    // Aquí podrías limpiar token de localStorage, etc.
    navigate('/');
  };

  return (
    <div className="home-container">
      {/* Cabecera similar a Dashboard/Mensajería */}
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true"></i>
        </div>
        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>
        <div className="right-section">
          <i className="fa fa-bell bell-icon" aria-hidden="true"></i>
          <button className="logout-btn" onClick={handleLogout}>
            <i className="fa fa-sign-out" aria-hidden="true"></i>
            <span>Salir</span>
          </button>
        </div>
      </header>

      {/* Contenido principal */}
      <main className="main-content">
        <h2 className="welcome-title">¡Bienvenido a JANUS!</h2>
        <p className="welcome-text">
          Selecciona una de las opciones para comenzar:
        </p>

        <div className="options-container">
          <button
            className="option-btn"
            onClick={() => navigate('/messaging')}
          >
            Mensajería
          </button>

          <button
            className="option-btn"
            onClick={() => navigate('/time-machine')}
          >
            Máquina del Tiempo
          </button>

          {/* Opciones futuras (deshabilitadas hasta su implementación) */}
          <button className="option-btn disabled-btn" disabled>
            Mi Planeta
          </button>
          <button className="option-btn disabled-btn" disabled>
            Misión: Yo puedo
          </button>
          <button className="option-btn disabled-btn" disabled>
            Mi diario de emociones
          </button>
        </div>
      </main>

      {/* Pie de página */}
      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default Home;
