// src/components/PatientAssignedRoutines.js
import React, { useState, useEffect } from 'react';
import { FaChevronDown, FaChevronUp, FaCheckCircle, FaRegCircle } from 'react-icons/fa';
import '../styles/routineTemplates.css';
import { useNavigate } from 'react-router-dom';

export default function PatientAssignedRoutines() {
  const [instances, setInstances] = useState([]);
  const [expanded, setExpanded]   = useState({});
  const [loading, setLoading]     = useState(true);
  const navigate = useNavigate();

  // Obtenemos user y token desde localStorage
  const user = JSON.parse(localStorage.getItem('user')) || {};
  const token = user.token;
  const patientId = user.id || user._id;

  useEffect(() => {
    if (!token || !patientId) {
      // No hay sesión, redirigimos al login
      return navigate('/login');
    }

    fetch(`/api/routines/instances/${patientId}`, {
      headers: { 'x-auth-token': token }
    })
      .then(res => {
        if (!res.ok) {
          if (res.status === 401) {
            // Token inválido o caducado
            localStorage.removeItem('user');
            return navigate('/login');
          }
          throw new Error(`Status ${res.status}`);
        }
        return res.json();
      })
      .then(data => {
        // Asegurarnos de que es un array
        if (Array.isArray(data)) {
          setInstances(data);
        } else {
          console.warn('Respuesta inesperada de instancias:', data);
          setInstances([]);
        }
      })
      .catch(err => {
        console.error('Error al cargar instancias:', err);
        setInstances([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [patientId, token, navigate]);

  const toggleExpand = id => {
    setExpanded(exp => ({ ...exp, [id]: !exp[id] }));
  };

  const markActivity = async (instanceId, idx) => {
    try {
      const res = await fetch(
        `/api/routines/instances/${instanceId}/activities/${idx}`,
        {
          method: 'POST',
          headers: { 'x-auth-token': token }
        }
      );
      if (!res.ok) throw new Error(`Status ${res.status}`);
      setInstances(insts =>
        insts.map(i =>
          i._id === instanceId
            ? {
                ...i,
                activities: i.activities.map((a, j) =>
                  j === idx ? { ...a, completed: !a.completed } : a
                )
              }
            : i
        )
      );
    } catch (err) {
      console.error('Error marcando actividad:', err);
    }
  };

  const completeAll = inst => {
    inst.activities.forEach((_, idx) => markActivity(inst._id, idx));
  };

  const postpone = instanceId => {
    setInstances(insts =>
      insts.map(i =>
        i._id === instanceId
          ? { ...i, status: 'postponed' }
          : i
      )
    );
  };

  if (loading) {
    return (
      <div className="patient-assigns tpl-container">
        <h1>Mis Plantillas Asignadas</h1>
        <p>Cargando tus plantillas…</p>
      </div>
    );
  }

  return (
    <div className="patient-assigns tpl-container">
      <h1>Mis Plantillas Asignadas</h1>

      {instances.length === 0 && (
        <div
          className="empty-state"
          style={{
            border: '2px dashed var(--primary-color)',
            padding: '2rem',
            textAlign: 'center',
            borderRadius: '8px',
            color: 'var(--primary-color)',
            fontSize: '1.2rem',
            marginTop: '2rem'
          }}
        >
          🤔 Aún no tienes plantillas asignadas
        </div>
      )}

      {instances.map(inst => {
        const total = inst.activities.length;
        const done  = inst.activities.filter(a => a.completed).length;
        const pct   = total ? (done / total) * 100 : 0;

        return (
          <div
            key={inst._id}
            className={`assign-card ${
              inst.status === 'completed'
                ? 'status-completed'
                : inst.status === 'postponed'
                ? 'status-postponed'
                : ''
            }`}
          >
            <div className="assign-header">
              <div>
                <h3>{inst.template.name}</h3>
                <small>{inst.template.description}</small>
              </div>
              <button
                className="btn-icon"
                onClick={() => toggleExpand(inst._id)}
              >
                {expanded[inst._id] ? <FaChevronUp /> : <FaChevronDown />}
              </button>
            </div>

            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{ width: `${pct}%` }}
              />
            </div>
            <div className="tpl-meta">
              {done} / {total} actividades completadas
            </div>

            <div className="assign-actions">
              {inst.status === 'pending' && (
                <>
                  <button
                    className="btn-complete"
                    onClick={() => completeAll(inst)}
                  >
                    Completar todo
                  </button>
                  <button
                    className="btn-postpone"
                    onClick={() => postpone(inst._id)}
                  >
                    Posponer
                  </button>
                </>
              )}
              {inst.status === 'completed' && <span>✔️ Completado</span>}
              {inst.status === 'postponed' && <span>⏳ Pospuesto</span>}
            </div>

            {expanded[inst._id] && (
              <div className="activities-list">
                {inst.activities.map((act, idx) => (
                  <div
                    key={idx}
                    className={`activity-line ${
                      act.completed ? 'completed' : ''
                    }`}
                  >
                    <div>
                      {act.completed ? <FaCheckCircle /> : <FaRegCircle />}
                      <span style={{ marginLeft: '0.5rem' }}>
                        {act.name}
                      </span>
                    </div>
                    {!act.completed && (
                      <button
                        className="btn-complete"
                        onClick={() => markActivity(inst._id, idx)}
                      >
                        Marcar
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}
