// src/components/PatientTemplates.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaChevronDown, FaChevronUp, FaCheckCircle, FaRegCircle } from 'react-icons/fa';
import './styles/routineTemplates.css';  // Estilos base de plantillas :contentReference[oaicite:0]{index=0}:contentReference[oaicite:1]{index=1}
import './styles/patientTemplates.css';  // Estilos específicos de paciente :contentReference[oaicite:2]{index=2}:contentReference[oaicite:3]{index=3}

export default function PatientTemplates() {
  const [instances, setInstances] = useState([]);
  const [expanded, setExpanded]   = useState({});
  const [filter, setFilter]       = useState('Todas');
  const [search, setSearch]       = useState('');

  // Extrae el ID del paciente del token JWT
  const token = localStorage.getItem('token');
  let userId = null;
  if (token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      userId = payload.id;
    } catch (e) {
      console.error('Error decodificando token', e);
    }
  }

  useEffect(() => {
    if (!userId) return;
    const headers = { 'x-auth-token': token };
    axios.get(`/api/routines/instances/${userId}`, { headers })  // GET /:patientId :contentReference[oaicite:4]{index=4}:contentReference[oaicite:5]{index=5}
      .then(res => setInstances(res.data))
      .catch(err => console.error('Error cargando plantillas asignadas', err));
  }, [userId, token]);

  const toggleExpand = id => {
    setExpanded(e => ({ ...e, [id]: !e[id] }));
  };

  const handleToggleActivity = (instId, idx, completed) => {
    const headers = { 'x-auth-token': token };
    axios.post(`/api/routines/instances/${instId}/activities/${idx}`, { completed }, { headers })  // marcar actividad :contentReference[oaicite:6]{index=6}:contentReference[oaicite:7]{index=7}
      .then(() => {
        setInstances(list => list.map(inst => {
          if (inst._id !== instId) return inst;
          const set = new Set(inst.completedActivities);
          if (completed) set.add(idx);
          else set.delete(idx);
          return { ...inst, completedActivities: Array.from(set) };
        }));
      })
      .catch(err => console.error('Error marcando actividad', err));
  };

  // Filtros
  const categories = ['Todas', ...new Set(instances.map(i => i.template.category))];
  const filtered = instances
    .filter(i => filter === 'Todas' || i.template.category === filter)
    .filter(i => i.template.name.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="patient-templates-page">
      <div className="pt-toolbar">
        <h1>Mis Plantillas</h1>
        <div>
          <select value={filter} onChange={e => setFilter(e.target.value)}>
            {categories.map(c => <option key={c}>{c}</option>)}
          </select>
          <input
            type="text"
            placeholder="Buscar..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{ marginLeft: '0.5rem', padding: '0.4rem', borderRadius: '4px', border:'1px solid #ccc' }}
          />
        </div>
      </div>

      {filtered.length === 0 ? (
        <div className="pt-empty">No hay plantillas que coincidan.</div>
      ) : (
        <div className="pt-list">
          {filtered.map(inst => {
            const tpl = inst.template;
            const doneCount = inst.completedActivities.length;
            const total = tpl.activities.length;
            const pct = Math.round((doneCount / total) * 100);
            const isOpen = expanded[inst._id];

            return (
              <div className="pt-card" key={inst._id}>
                <div className="pt-header">
                  <div>
                    <h2>{tpl.name}</h2>
                    <small>Asignada: {new Date(inst.assignedAt).toLocaleDateString()}</small>
                    <div className="pt-progress">
                      <div className="pt-bar" style={{ width: `${pct}%` }} />
                      <span>{pct}%</span>
                    </div>
                  </div>
                  <button className="pt-expand" onClick={() => toggleExpand(inst._id)}>
                    {isOpen ? <FaChevronUp/> : <FaChevronDown/>}
                  </button>
                </div>

                {isOpen && (
                  <ul className="pt-activities">
                    {tpl.activities.map((act, idx) => {
                      const done = inst.completedActivities.includes(idx);
                      return (
                        <li key={idx} className={done ? 'done' : ''}>
                          <button
                            className="pt-check"
                            onClick={() => handleToggleActivity(inst._id, idx, !done)}
                          >
                            {done ? <FaCheckCircle/> : <FaRegCircle/>}
                          </button>
                          <div className="pt-desc">
                            <span className="pt-name">{act.name}</span>
                            <span className="pt-detail">{act.desc}</span>
                            <span className="pt-detail">Reto: {act.challenge}</span>
                            <span className="pt-detail">Duración: {act.minutes} min</span>
                          </div>
                        </li>
                      );
                    })}
                  </ul>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
