import React from 'react';
import { Link } from 'react-router-dom';
import './home.css';

// Imagen de perfil de ejemplo
import defaultProfile from '../assets/default-profile.png'; // Ajusta la ruta según tu estructura

function Home() {

  // Ejemplo de datos de usuario (puedes traerlos de tu contexto o props)
  const user = {
    name: 'Iván',
    profileImage: defaultProfile, // Podrías usar una URL dinámica
  };

  return (
    <div className="home-container">
      {/* Miniatura de perfil en la esquina */}
      <div className="mini-profile-corner">
        <img
          src={user.profileImage}
          alt="Foto de perfil"
          className="mini-profile-image"
        />
        <span className="mini-profile-name">{user.name}</span>
      </div>

      {/* Cuadro principal de perfil */}
      <div className="profile-box">
        <div className="profile-header">
          <img
            src={user.profileImage}
            alt="Foto de perfil"
            className="profile-image"
          />
          <div className="profile-info">
            <h2>{user.name}</h2>
            <p>Datos personales (ej. Email, Teléfono...)</p>
            {/* Enlace a la página de edición de perfil */}
            <Link to="/edit-profile" className="edit-profile-btn">
              Editar Perfil
            </Link>
          </div>
        </div>
      </div>

      {/* Opciones en círculos */}
      <div className="options-circles">
        <div className="circle-option">
          <i className="fa fa-bell icon"></i>
          <p>Gestionar Recordatorios</p>
        </div>
        <div className="circle-option">
          <i className="fa fa-envelope icon"></i>
          <p>Correo</p>
        </div>
        <div className="circle-option">
          <i className="fa fa-calendar-check-o icon"></i>
          <p>Planificación de Tareas</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
