// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTrash, FaEdit } from 'react-icons/fa';
import axios from 'axios';
import '../styles/routineTemplates.css';

export default function RoutineTemplatesPage() {
  const [templates, setTemplates]     = useState([]);
  const [patients, setPatients]       = useState([]);
  const [loading, setLoading]         = useState(true);
  const [error, setError]             = useState(null);
  const [assignments, setAssignments] = useState({});

  const [modalData, setModalData] = useState({
    visible:  false,
    loading:  false,
    error:    null,
    patient:  null,
    instance: null
  });

  const navigate = useNavigate();
  const token    = localStorage.getItem('token');
  const headers  = { 'Content-Type': 'application/json', 'x-auth-token': token };

  // Fetch inicial de plantillas, pacientes y assignments
  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }

    Promise.all([
      fetch('/api/routines/templates',   { headers }),
      fetch('/api/therapist/patients',   { headers })
    ])
      .then(async ([resTpl, resPat]) => {
        if (resTpl.status === 401 || resPat.status === 401) {
          alert('Sesi√≥n expirada. Vuelve a iniciar sesi√≥n.');
          navigate('/login');
          return;
        }
        const dataTpl = await resTpl.json();
        const dataPat = await resPat.json();
        if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
          throw new Error('Respuesta inesperada del servidor');
        }

        setTemplates(dataTpl);
        setPatients(dataPat);

        // Para cada plantilla, cargamos las instancias ya asignadas
        const resp = await Promise.all(
          dataTpl.map(async tpl => {
            const { data: insts } = await axios.get(
              `/api/routines/instances/template/${tpl._id}`,
              { headers }
            );
            return [tpl._id, insts];
          })
        );
        setAssignments(Object.fromEntries(resp));
      })
      .catch(err => {
        console.error(err);
        setError(err.message);
      })
      .finally(() => setLoading(false));
  }, [navigate, token]);

  // Drag & drop handlers
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };
  const allowDrop = e => e.preventDefault();

  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));

    // eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, { method: 'DELETE', headers })
        .catch(console.error);
    }
    // duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      const payload = {
        name:       item.name + ' (copia)',
        description:item.description,
        category:   item.category,
        tags:       item.tags,
        duration:   item.duration,
        reminder:   item.reminder,
        activities: item.activities.map(a => ({
          name:      a.name,
          desc:      a.desc,
          challenge: a.challenge,
          minutes:   a.minutes
        }))
      };
      fetch('/api/routines/templates', {
        method: 'POST', headers, body: JSON.stringify(payload)
      })
        .then(res => {
          if (!res.ok) return res.text().then(txt => { throw new Error(txt) });
          return res.json();
        })
        .then(newTpl => setTemplates(ts => [newTpl, ...ts]))
        .catch(err => {
          console.error('Error duplicando plantilla:', err);
          alert('No se pudo duplicar: ' + err.message);
        });
    }
    // asignar a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch('/api/routines/instances', {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id, patientId })
      })
        .then(res => {
          if (!res.ok) throw new Error(`HTTP ${res.status}`);
          alert(`Plantilla ¬´${item.name}¬ª asignada a paciente ${patientId}`);
        })
        .catch(err => {
          console.error('Error asignando plantilla:', err);
          alert('No se pudo asignar: ' + err.message);
        });
    }
  }, [token]);

  // Abre modal con detalle de un paciente para una plantilla dada
  const openPatientDetail = async (patient, templateId) => {
    setModalData({ visible:true, loading:true, error:null, patient, instance:null });
    try {
      const { data } = await axios.get(
        `/api/routines/instances/${patient._id}`,
        { headers }
      );
      const list = Array.isArray(data) ? data : (data.instances || []);
      const inst = list.find(i => i.template._id === templateId);
      if (!inst) throw new Error('No existe asignaci√≥n para este paciente.');
      setModalData(mod => ({ ...mod, instance:inst, loading:false }));
    } catch (err) {
      console.error(err);
      setModalData(mod => ({
        ...mod,
        loading:false,
        error: err.response?.data?.msg || err.message
      }));
    }
  };

  const closeModal = () => {
    setModalData({ visible:false, loading:false, error:null, patient:null, instance:null });
  };

  if (loading) return <div className="tpl-container">Cargando plantillas‚Ä¶</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page therapist-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
            üìÑ Nueva plantilla
          </button>
        </div>

        {templates.length === 0 ? (
          <div className="empty-state">
            ü§î A√∫n no se han creado plantillas
          </div>
        ) : (
          templates.map(tpl => (
            <div
              key={tpl._id}
              className="tpl-card"
              draggable
              onDragStart={e => onDragStart(e, tpl, 'template')}
            >
              <FaEdit
                className="icon-edit"
                onClick={() => navigate(`/therapist/templates/${tpl._id}/edit`)}
              />
              <h2>{tpl.name}</h2>
              <p className="tpl-desc">{tpl.description}</p>
              <div className="tpl-meta">
                {tpl.activities.length} actividades
              </div>

              <h3 style={{ marginTop: '2rem' }}>Pacientes</h3>
              <div className="tpl-grid patient-grid">
                {patients.map(p => (
                  <div
                    key={p._id}
                    className="patient-card"
                    onDragOver={allowDrop}
                    onDrop={e => handleDrop(e, `assign-${p._id}`)}
                    onClick={() => openPatientDetail(p, tpl._id)}
                  >
                    <img
                      src={p.avatar}
                      alt={p.name}
                      className="patient-avatar"
                    />
                    <div className="patient-info">
                      <strong>{p.name}</strong>
                      <span className="patient-email">{p.email}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}

        {/* Modal de detalle */}
        {modalData.visible && (
          <div className="modal-backdrop" onClick={closeModal}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
              <button className="modal-close" onClick={closeModal}>√ó</button>

              {modalData.loading && <p>Cargando detalle‚Ä¶</p>}
              {modalData.error   && <p className="error-msg">{modalData.error}</p>}

              {modalData.instance && (() => {
                const inst    = modalData.instance;
                const tpl     = inst.template;
                const total   = tpl.activities.length;
                const done    = inst.completedActivities.length;
                const pct     = Math.round((done / total) * 100);

                return (
                  <>
                    <h2>
                      {modalData.patient.name} ‚Äî {tpl.name}
                    </h2>

                    <div className="progress-bar" style={{ margin: '0.5rem 0' }}>
                      <div className="progress-filled" style={{ width: `${pct}%` }} />
                    </div>
                    <p className="progress-text">
                      {done} de {total} actividades completadas ({pct}%)
                    </p>

                    <ul className="tpl-activities-list">
                      {tpl.activities.map((act, i) => {
                        const doneAct = inst.completedActivities.includes(i);
                        return (
                          <li key={i} className="activity-item">
                            <span className="activity-toggle">
                              {doneAct ? '‚úîÔ∏è' : '‚óªÔ∏è'}
                            </span>
                            <span className={doneAct ? 'completed' : ''}>
                              {act.name}
                            </span>
                            <small className="activity-meta">
                              {act.minutes} min | Nivel {act.challenge}
                            </small>
                          </li>
                        );
                      })}
                    </ul>
                  </>
                );
              })()}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
