import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import chatService from '../services/chatService';
import '../styles/messaging.css';

function Messaging() {
  const navigate = useNavigate();

  // Estado local
  const [chats, setChats] = useState([]);
  const [selectedChatId, setSelectedChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]); // Para el dropdown
  const [showDropdown, setShowDropdown] = useState(false); // Controlar visibilidad del dropdown
  const [newMessage, setNewMessage] = useState('');
  const [file, setFile] = useState(null);

  // Cargar la lista de chats al montar
  useEffect(() => {
    async function fetchChats() {
      try {
        const data = await chatService.getChats();
        setChats(data);
      } catch (err) {
        console.error('Error obteniendo chats:', err);
      }
    }
    fetchChats();
  }, []);

  // Cargar mensajes cuando se seleccione un chat
  useEffect(() => {
    if (selectedChatId) {
      async function fetchMessages() {
        try {
          const data = await chatService.getMessages(selectedChatId);
          setMessages(data);
        } catch (err) {
          console.error('Error obteniendo mensajes:', err);
        }
      }
      fetchMessages();
    } else {
      setMessages([]);
    }
  }, [selectedChatId]);

  // Manejo de selecci칩n de chat
  const handleSelectChat = (chatId) => {
    setSelectedChatId(chatId);
  };

  // Manejo de b칰squeda (autocompletado)
  const handleSearchChange = async (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    // Si hay algo que buscar, llamamos a searchUsers
    if (query.trim().length > 0) {
      setShowDropdown(true);
      try {
        // Aseg칰rate de pasar el token si tu backend lo requiere
        const usersFound = await chatService.searchUsers(query.trim());
        setSearchResults(usersFound);
      } catch (err) {
        console.error('Error buscando usuarios:', err);
      }
    } else {
      // Si se limpia el campo, ocultamos el dropdown
      setShowDropdown(false);
      setSearchResults([]);
    }
  };

  // Manejo de selecci칩n de usuario en el dropdown
  const handleSelectUser = async (user) => {
    try {
      // user._id o user.id, seg칰n lo que retorne el backend
      const newChat = await chatService.startChat(user._id);

      // Actualizar la lista de chats
      setChats((prev) => [...prev, newChat]);
      setSelectedChatId(newChat.id); // O newChat._id, seg칰n retorne
      // Limpiamos b칰squeda
      setSearchQuery('');
      setSearchResults([]);
      setShowDropdown(false);
    } catch (err) {
      console.error('Error iniciando chat:', err);
    }
  };

  // Manejo de archivo adjunto
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setFile(e.target.files[0]);
    }
  };

  // Enviar mensaje en el chat seleccionado
  const handleSendMessage = async () => {
    if (!selectedChatId || !newMessage.trim()) return;
    try {
      const formData = new FormData();
      formData.append('text', newMessage);
      if (file) {
        formData.append('file', file);
      }
      const msgCreated = await chatService.sendMessage(selectedChatId, formData);
      setMessages((prev) => [...prev, msgCreated]);
      // Limpiar campos
      setNewMessage('');
      setFile(null);
    } catch (err) {
      console.error('Error enviando mensaje:', err);
    }
  };

  // Cerrar sesi칩n
  const handleLogout = () => {
    // Limpieza de tokens, etc.
    navigate('/');
  };

  return (
    <div className="messaging-container">
      {/* Cabecera en morado */}
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true"></i>
        </div>
        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>
        <div className="right-section">
          <button className="logout-btn" onClick={handleLogout}>
            Cerrar Sesi칩n
          </button>
        </div>
      </header>

      <main className="messaging-main">
        {/* Columna izquierda: Lista de chats + B칰squeda */}
        <div className="chat-list-container">
          {/* Barra de b칰squeda */}
          <div className="search-bar" style={{ position: 'relative' }}>
            <input
              type="text"
              placeholder="To: Enviar a..."
              value={searchQuery}
              onChange={handleSearchChange}
              onFocus={() => setShowDropdown(true)}
            />
            {/* Desplegable con resultados */}
            {showDropdown && searchResults.length > 0 && (
              <ul className="search-dropdown">
                {searchResults.map((user) => (
                  <li
                    key={user._id}
                    onClick={() => handleSelectUser(user)}
                  >
                    {user.nombre} {user.apellidos} ({user.email})
                  </li>
                ))}
              </ul>
            )}
          </div>

          {chats.length === 0 ? (
            <div className="no-chats">
              <p>Comienza a conversar ahora con tus especialistas! <span>游땕</span></p>
            </div>
          ) : (
            <ul className="chat-list">
              {chats.map((chat) => (
                <li
                  key={chat.id}
                  className={`chat-item ${chat.id === selectedChatId ? 'selected' : ''}`}
                  onClick={() => handleSelectChat(chat.id)}
                >
                  <div className="chat-avatar">
                    <i className="fa fa-user-circle" aria-hidden="true"></i>
                  </div>
                  <div className="chat-info">
                    <p className="chat-name">
                      {chat.otherUserName} ({chat.otherUserRole})
                    </p>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Columna derecha: Detalle del chat */}
        <div className="chat-detail">
          {selectedChatId ? (
            <>
              <div className="messages-area">
                {messages.map((msg, idx) => (
                  <div
                    key={idx}
                    className={`message-item ${
                      msg.senderId === 'CURRENT_USER_ID'
                        ? 'message-outgoing'
                        : 'message-incoming'
                    }`}
                  >
                    <p className="message-sender">{msg.senderName}:</p>
                    <p className="message-text">{msg.text}</p>
                    {msg.fileUrl && (
                      <div className="message-file">
                        <a href={msg.fileUrl} download>
                          {msg.fileName || 'Descargar archivo'}
                        </a>
                      </div>
                    )}
                  </div>
                ))}
              </div>
              <div className="new-message-area">
                <label htmlFor="fileInput" className="attach-btn">
                  <i className="fa fa-paperclip" aria-hidden="true"></i>
                </label>
                <input
                  type="file"
                  id="fileInput"
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
                <input
                  type="text"
                  className="message-input"
                  placeholder="Escribe algo..."
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                />
                <button className="send-btn" onClick={handleSendMessage}>Enviar</button>
              </div>
            </>
          ) : (
            <div className="no-chat-selected">
              <p>Selecciona un chat para ver los mensajes</p>
            </div>
          )}
        </div>
      </main>

      <footer className="footer-bar">
        <p>2025 춸 Iv치n Vela Campos</p>
      </footer>
    </div>
  );
}

export default Messaging;
