// src/components/TherapistTemplates.js
import React, { useState, useEffect } from 'react';
import { useNavigate }           from 'react-router-dom';
import axios                     from 'axios';
import './styles/routineTemplates.css';  // estilos :contentReference[oaicite:0]{index=0}:contentReference[oaicite:1]{index=1}

export default function TherapistTemplates() {
  const [templates, setTemplates] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      const token = localStorage.getItem('token');
      const { data } = await axios.get('/api/routines/templates', {
        headers: { 'x-auth-token': token }
      });
      setTemplates(data);
    })();
  }, []);

  return (
    <div className="tpl-container">
      <div className="tpl-header">
        <h1>Mis Plantillas de Actividades</h1>
        <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
          + Nueva Plantilla
        </button>
      </div>
      <div className="tpl-grid">
        {templates.map(t => (
          <div key={t._id} className="tpl-card">
            <h2>{t.name}</h2>
            <p className="tpl-desc">{t.description}</p>
            <small className="tpl-meta">
              {t.activities.length} actividades â€¢ Creada {new Date(t.createdAt).toLocaleDateString()}
            </small>
            <button className="btn-assign" onClick={() => navigate(`/therapist/templates/${t._id}/assign`)}>
              Asignar a Paciente
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
