// src/pages/RoutineTemplatesPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaTrash, FaEdit } from 'react-icons/fa';
import '../styles/routineTemplates.css';
import '../styles/patientsList.css'; // para .modal-overlay y .modal-content

export default function RoutineTemplatesPage() {
  const [templates, setTemplates]         = useState([]);
  const [patients, setPatients]           = useState([]);
  const [assignments, setAssignments]     = useState({}); // { [tplId]: [instances] }
  const [modalData, setModalData]         = useState(null);
  const [loading, setLoading]             = useState(true);
  const [error, setError]                 = useState(null);
  const navigate                           = useNavigate();
  const token                              = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      setLoading(false);
      return;
    }
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    Promise.all([
      fetch('/api/routines/templates', { headers }),
      fetch('/api/therapist/patients',   { headers })
    ])
      .then(async ([resTpl, resPat]) => {
        if (resTpl.status === 401 || resPat.status === 401) {
          alert('Sesión expirada. Vuelve a iniciar sesión.');
          navigate('/login');
          setLoading(false);
          return;
        }
        const dataTpl = await resTpl.json();
        const dataPat = await resPat.json();
        if (!Array.isArray(dataTpl) || !Array.isArray(dataPat)) {
          throw new Error('Respuesta inesperada del servidor');
        }
        setTemplates(dataTpl);
        setPatients(dataPat);

        // 2) Para cada plantilla, obtener instancias (pacientes asignados)
        const resp = await Promise.all(
          dataTpl.map(async tpl => {
            const { data: insts } = await axios.get(
              `/api/routines/instances/template/${tpl._id}`,
              { headers }
            );
            return [tpl._id, insts];
          })
        );
        setAssignments(Object.fromEntries(resp));
      })
      .catch(err => {
        console.error(err);
        setError(err.message || 'Error cargando datos');
      })
      .finally(() => {
        setLoading(false);
      });
  }, [navigate, token]);

  // Modal
  const handlePatientClick = (template, instance) => {
    setModalData({ template, instance });
  };
  const closeModal = () => setModalData(null);

  // Drag & Drop handlers
  const onDragStart = (e, item, type) => {
    e.dataTransfer.setData('application/json', JSON.stringify({ type, item }));
    e.dataTransfer.effectAllowed = 'copyMove';
  };
  const allowDrop = e => e.preventDefault();
  const handleDrop = useCallback((e, zone) => {
    e.preventDefault();
    const { type, item } = JSON.parse(e.dataTransfer.getData('application/json'));
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };

    // Eliminar plantilla
    if (zone === 'trash' && type === 'template') {
      setTemplates(ts => ts.filter(t => t._id !== item._id));
      fetch(`/api/routines/templates/${item._id}`, { method: 'DELETE', headers })
        .catch(console.error);
    }
    // Duplicar plantilla
    else if (zone === 'duplicate' && type === 'template') {
      const payload = {
        name:        item.name + ' (copia)',
        description: item.description,
        category:    item.category,
        tags:        item.tags,
        duration:    item.duration,
        reminder:    item.reminder,
        activities:  item.activities.map(a => ({
          name:      a.name,
          desc:      a.desc,
          challenge: a.challenge,
          minutes:   a.minutes
        }))
      };
      fetch('/api/routines/templates', {
        method: 'POST',
        headers,
        body: JSON.stringify(payload)
      })
        .then(res => {
          if (!res.ok) return res.text().then(text => { throw new Error(text) });
          return res.json();
        })
        .then(newTpl => setTemplates(ts => [newTpl, ...ts]))
        .catch(err => {
          console.error('Error duplicando plantilla:', err);
          alert('No se pudo duplicar: ' + err.message);
        });
    }
    // Asignar a paciente
    else if (zone.startsWith('assign-') && type === 'template') {
      const patientId = zone.split('-')[1];
      fetch('/api/routines/instances', {
        method: 'POST',
        headers,
        body: JSON.stringify({ templateId: item._id, patientId })
      })
        .then(res => {
          if (!res.ok) throw new Error(`HTTP ${res.status}`);
          alert(`Plantilla «${item.name}» asignada a paciente ${patientId}`);
        })
        .catch(err => {
          console.error('Error asignando plantilla:', err);
          alert('No se pudo asignar: ' + err.message);
        });
    }
  }, [token]);

  if (loading) return <div className="tpl-container">Cargando plantillas…</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="tpl-container">
      {/* Header */}
      <div className="tpl-header">
        <h1>Mis Plantillas</h1>
        <button className="btn-new" onClick={() => navigate('/therapist/templates/new')}>
          + Nueva plantilla
        </button>
      </div>

      {/* Grilla de plantillas */}
      {templates.length === 0 ? (
        <div className="empty-state">
          🤔 Aún no se han creado plantillas
        </div>
      ) : (
        <div className="tpl-grid">
          {templates.map(tpl => (
            <div
              key={tpl._id}
              className="tpl-card"
              draggable
              onDragStart={e => onDragStart(e, tpl, 'template')}
            >
              <FaEdit
                className="icon-edit"
                onClick={() => navigate(`/therapist/templates/${tpl._id}/edit`)}
              />
              <h2>{tpl.name}</h2>
              <p className="tpl-desc">{tpl.description}</p>
              <div className="tpl-meta">{tpl.activities.length} actividades</div>

              {/* Mini-grilla de pacientes asignados */}
              <div className="patient-grid">
                {(assignments[tpl._id] || []).map(inst => (
                  <div
                    key={inst._id}
                    className="patient-card"
                    onClick={() => handlePatientClick(tpl, inst)}
                    onDragOver={allowDrop}
                    onDrop={e => handleDrop(e, `assign-${inst.patient._id}`)}
                    title="Ver progreso de este paciente"
                  >
                    {inst.patient.nombre} {inst.patient.apellidos}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Zonas de DROP */}
      <div className="dropzones">
        <div className="dropzone trash" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'trash')}>
          🗑️ Arrastra aquí para eliminar
        </div>
        <div className="dropzone duplicate" onDragOver={allowDrop} onDrop={e => handleDrop(e, 'duplicate')}>
          📋 Arrastra aquí para duplicar
        </div>
      </div>

      {/* Modal de detalle de instancia */}
      {modalData && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>
              Progreso de {modalData.instance.patient.nombre}{' '}
              {modalData.instance.patient.apellidos}
            </h2>
            <ul className="assignment-detail-list">
              {modalData.template.activities.map((act, idx) => {
                const done = modalData.instance.completedActivities.includes(idx);
                return (
                  <li key={idx} className={done ? 'done' : 'pending'}>
                    <i
                      className={done ? 'fa fa-check-circle' : 'fa fa-circle-o'}
                      aria-hidden="true"
                    ></i>{' '}
                    <strong>{act.name}</strong> – {act.desc}
                  </li>
                );
              })}
            </ul>
            <button className="close-modal-btn" onClick={closeModal}>
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
