import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/therapistDashboardHome.css';

function TherapistDashboard() {
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Definir los botones del menú
  const menuButtons = [
    { label: "Lista de Pacientes", icon: "fa fa-users", route: "/therapist/patients" },
    { label: "Rutinas y Tiempo", icon: "fa fa-clock-o", route: "/therapist/routines" },
    { label: "Intereses", icon: "fa fa-heart", route: "/therapist/interests" },
    { label: "Satisfacción", icon: "fa fa-smile-o", route: "/therapist/satisfaction" },
    { label: "Mensajería", icon: "fa fa-comments", route: "/messaging" },
    { label: "Generar Invitación", icon: "fa fa-envelope", route: "/therapist/invite" }
  ];

  const handleButtonClick = (route) => {
    navigate(route);
  };

  return (
    <div className="therapist-dashboard-container">
      <header className="top-bar">
        <div className="left-section">
          <i className="fa fa-user-circle user-icon" aria-hidden="true"></i>
        </div>
        <div className="center-section">
          <h1 className="title">Dashboard Terapeuta</h1>
        </div>
        <div className="right-section">
          <button className="logout-btn" onClick={() => navigate('/')}>
            <i className="fa fa-sign-out" aria-hidden="true"></i>
            Salir
          </button>
        </div>
      </header>

      <main className="main-content">
        <section className="buttons-section">
          {menuButtons.map((btn, index) => (
            <button key={index} className="option-btn" onClick={() => handleButtonClick(btn.route)}>
              <i className={`${btn.icon} icon`} aria-hidden="true"></i>
              <span>{btn.label}</span>
            </button>
          ))}
        </section>
        <section className="calendar-section">
          <Calendar onChange={handleDateChange} value={selectedDate} />
        </section>
      </main>

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}

export default TherapistDashboard;
