import React, { useState, useEffect, useCallback } from 'react';
// Usar require para evitar problemas de default export
const jwt_decode = require('jwt-decode');
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';
import '../styles/routineTemplates.css';

export default function PatientTemplatesPage() {
  const [instances, setInstances] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterText, setFilterText] = useState('');
  const [sortOrder, setSortOrder] = useState('newest');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    let patientId;
    try {
      const payload = jwt_decode(token);
      patientId = payload.id || payload.userId || payload._id;
    } catch (e) {
      console.error('Token invÃ¡lido', e);
      navigate('/login');
      return;
    }

    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };
    fetch(`/api/routines/instances/${patientId}`, { headers })
      .then(async res => {
        if (res.status === 401) throw new Error('SesiÃ³n expirada');
        const data = await res.json();
        if (!Array.isArray(data)) throw new Error('Respuesta inesperada');
        setInstances(data.map(inst => ({ ...inst, expanded: false })));
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [navigate]);

  const handleToggleExpand = idx => {
    setInstances(instances.map((inst, i) => i === idx ? { ...inst, expanded: !inst.expanded } : inst));
  };

  const handleActivityToggle = (instanceId, idx, checked) => {
    const token = localStorage.getItem('token');
    const headers = { 'Content-Type': 'application/json', 'x-auth-token': token };
    fetch(`/api/routines/instances/${instanceId}/activities/${idx}`, {
      method: 'POST', headers, body: JSON.stringify({ completed: checked })
    })
      .then(async res => {
        if (!res.ok) throw new Error('Error marcando actividad');
        // Actualizar estado local
        setInstances(instances.map(inst => {
          if (inst._id !== instanceId) return inst;
          const completed = new Set(inst.completedActivities);
          if (checked) completed.add(idx);
          else completed.delete(idx);
          return { ...inst, completedActivities: Array.from(completed) };
        }));
      })
      .catch(err => alert(err.message));
  };

  const filtered = instances
    .filter(inst => inst.template.name.toLowerCase().includes(filterText.toLowerCase()));

  const sorted = filtered.sort((a, b) => {
    const da = new Date(a.createdAt || a.template.createdAt);
    const db = new Date(b.createdAt || b.template.createdAt);
    return sortOrder === 'newest' ? db - da : da - db;
  });

  if (loading) return <div className="tpl-container">Cargando rutinasâ€¦</div>;
  if (error)   return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Rutinas</h1>
          <div className="controls">
            <input
              type="text"
              placeholder="Buscar..."
              value={filterText}
              onChange={e => setFilterText(e.target.value)}
            />
            <select value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
              <option value="newest">MÃ¡s recientes</option>
              <option value="oldest">MÃ¡s antiguas</option>
            </select>
          </div>
        </div>

        {sorted.length === 0 ? (
          <div className="empty-state" style={{ marginTop: '2rem' }}>
            ðŸ˜´ No tienes rutinas asignadas
          </div>
        ) : (
          <div className="tpl-grid">
            {sorted.map((inst, i) => (
              <div key={inst._id} className="tpl-card patient-card">
                <h2>{inst.template.name}</h2>
                <p className="tpl-desc">{inst.template.description}</p>
                <div className="tpl-meta">
                  {inst.completedActivities.length}/{inst.template.activities.length} completadas
                </div>
                <button
                  className="btn-new"
                  onClick={() => handleToggleExpand(i)}
                  style={{ margin: '0.5rem 0' }}
                >
                  Detalles {inst.expanded ? <FaChevronUp/> : <FaChevronDown/>}
                </button>

                {inst.expanded && (
                  <ul className="activities-list" style={{ padding: 0, listStyle: 'none' }}>
                    {inst.template.activities.map((act, idx) => (
                      <li key={idx} className="activity-item">
                        <label>
                          <input
                            type="checkbox"
                            checked={inst.completedActivities.includes(idx)}
                            onChange={e => handleActivityToggle(inst._id, idx, e.target.checked)}
                          />{' '}
                          <strong>{act.name}</strong> - {act.desc} ({act.minutes} min)
                        </label>
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
