// src/pages/PatientTemplatesPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';
import '../styles/routineTemplates.css';
import { FaRegCircle, FaCheckCircle, FaSearch, FaSort } from 'react-icons/fa';

export default function PatientTemplatesPage() {
  const [instances, setInstances] = useState([]);
  const [filtered, setFiltered]   = useState([]);
  const [loading, setLoading]     = useState(true);
  const [error, setError]         = useState(null);
  const [search, setSearch]       = useState('');
  const [sortKey, setSortKey]     = useState('name');
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  let patientId;
  try {
    patientId = jwt_decode(token).user.id;
  } catch {
    navigate('/login');
  }
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  // Fetch assigned templates
  useEffect(() => {
    if (!token) return navigate('/login');

    setLoading(true);
    fetch(`/api/routines/instances/${patientId}`, { headers })
      .then(async res => {
        if (res.status === 401) {
          alert('SesiÃ³n expirada. Por favor inicia sesiÃ³n de nuevo.');
          return navigate('/login');
        }
        if (!res.ok) throw new Error(await res.text());
        return res.json();
      })
      .then(data => {
        setInstances(data);
        setFiltered(data);
      })
      .catch(err => setError(err.message))
      .finally(() => setLoading(false));
  }, [patientId, token, navigate]);

  // Calculate completion percentage
  const progress = inst =>
    inst.activities.length === 0
      ? 0
      : Math.round(
          (inst.activities.filter(a => a.completed).length /
            inst.activities.length) *
            100
        );

  // Filter & sort
  useEffect(() => {
    let data = instances.filter(inst =>
      inst.template.name.toLowerCase().includes(search.toLowerCase())
    );
    data.sort((a, b) => {
      if (sortKey === 'name')
        return a.template.name.localeCompare(b.template.name);
      if (sortKey === 'progress')
        return progress(b) - progress(a);
      return 0;
    });
    setFiltered(data);
  }, [search, sortKey, instances]);

  // Toggle activity completion
  const toggleActivity = (instanceId, idx) => {
    fetch(`/api/routines/instances/${instanceId}/activities/${idx}`, {
      method: 'POST',
      headers
    })
      .then(async res => {
        if (res.status === 401) throw new Error('SesiÃ³n expirada');
        if (!res.ok) throw new Error(await res.text());
        return res.json();
      })
      .then(updated => {
        setInstances(insts =>
          insts.map(i => (i._id === instanceId ? updated : i))
        );
      })
      .catch(err => alert('Error actualizando actividad: ' + err.message));
  };

  if (loading) return <div className="tpl-container">Cargando plantillasâ€¦</div>;
  if (error) return <div className="tpl-container">Error: {error}</div>;

  return (
    <div className="routine-templates-page">
      <div className="tpl-container">
        <div className="tpl-header">
          <h1>Mis Plantillas</h1>
          <div className="search-sort">
            <input
              className="search-input"
              type="text"
              value={search}
              onChange={e => setSearch(e.target.value)}
              placeholder="ðŸ” Buscar..."
            />
            <FaSearch className="search-icon" />
            <select
              className="sort-select"
              value={sortKey}
              onChange={e => setSortKey(e.target.value)}
            >
              <option value="name">Nombre</option>
              <option value="progress">Progreso</option>
            </select>
            <FaSort className="sort-icon" />
          </div>
        </div>

        {filtered.length === 0 ? (
          <div className="empty-state" style={{ marginTop: '2rem' }}>
            ðŸ¤” No tienes plantillas asignadas
          </div>
        ) : (
          <div className="tpl-grid">
            {filtered.map(inst => (
              <div key={inst._id} className="tpl-card">
                <h2>{inst.template.name}</h2>
                <p className="tpl-desc">{inst.template.description}</p>
                {/* Progress Bar */}
                <div className="progress-bar">
                  <div
                    className="progress-fill"
                    style={{ width: `${progress(inst)}%` }}
                  />
                </div>
                <div className="tpl-meta">
                  {progress(inst)}% completado â€” Asignada:{' '}
                  {new Date(inst.assignedAt).toLocaleDateString()}
                </div>

                {/* Activities List */}
                <ul className="activity-list">
                  {inst.activities.map((act, idx) => (
                    <li key={idx} className="activity-item">
                      <button
                        className="activity-toggle"
                        onClick={() => toggleActivity(inst._id, idx)}
                      >
                        {act.completed ? (
                          <FaCheckCircle />
                        ) : (
                          <FaRegCircle />
                        )}
                      </button>
                      <div className="activity-info">
                        <span className="activity-name">{act.name}</span>
                        <span className="activity-minutes">
                          {act.minutes} min
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
