// src/components/Dashboard.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate }                      from 'react-router-dom';
import Calendar                              from 'react-calendar';
import axios                                 from 'axios';
import 'react-calendar/dist/Calendar.css';
import '../styles/home.css';
import '../styles/taskPlanner.css';

// Decodifica JWT
function decodeJwt(token) {
  try {
    const b64 = token.split('.')[1].replace(/-/g,'+').replace(/_/g,'/');
    return JSON.parse(atob(b64));
  } catch { return null; }
}
function getUserId() {
  const raw = localStorage.getItem('token');
  if (!raw) return null;
  const token  = raw.startsWith('Bearer ') ? raw.slice(7) : raw;
  const payload = decodeJwt(token);
  return payload?.id || payload?.sub || payload?.user?.id || null;
}

export default function Home() {
  const navigate = useNavigate();

  // Calendario y recordatorios
  const [selectedDate, setSelectedDate]     = useState(new Date());
  const [tasks, setTasks]                   = useState([]);
  const [upcoming, setUpcoming]             = useState([]);
  const [expiredReminders, setExpiredReminders] = useState([]);
  const [countdowns, setCountdowns]         = useState({});
  const timerRef = useRef();

  // Modals
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showManage, setShowManage]           = useState(false);
  const [newTitle, setNewTitle]               = useState('');
  const [newDateTime, setNewDateTime]         = useState('');
  const [editStates, setEditStates]           = useState({});

  // Perfil paciente
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [profilePhoto, setProfilePhoto]         = useState(null);
  const [newPhotoFile, setNewPhotoFile]         = useState(null);

  useEffect(() => {
    // Carga foto de perfil
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const { data } = await axios.get('/api/users/me', {
          headers: { 'x-auth-token': token }
        });
        setProfilePhoto(data.photoUrl);
      } catch {}
    };
    fetchProfile();
  }, []);

  const handleFileChange = e => {
    const file = e.target.files[0];
    if (file) {
      setNewPhotoFile(file);
      setProfilePhoto(URL.createObjectURL(file));
    }
  };
  const handleUploadPhoto = async () => {
    if (!newPhotoFile) return;
    const formData = new FormData();
    formData.append('photo', newPhotoFile);
    try {
      const token = localStorage.getItem('token');
      const { data } = await axios.post('/api/users/me/photo', formData, {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'multipart/form-data'
        }
      });
      setProfilePhoto(data.photoUrl);
      setShowProfileModal(false);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch reminders
  useEffect(() => {
    const fetchTasks = async () => {
      const token = localStorage.getItem('token');
      const userId = getUserId();
      if (!token || !userId) return;
      try {
        const { data } = await axios.get('/api/tasks', {
          headers: { 'x-auth-token': token }
        });
        const all = Array.isArray(data) ? data : [];
        setTasks(all);
        const now = Date.now();
        setExpiredReminders(
          all.filter(t => new Date(t.startTime).getTime() <= now)
             .sort((a,b) => new Date(b.startTime) - new Date(a.startTime))
        );
        setUpcoming(
          all.filter(t => new Date(t.startTime).getTime() > now)
             .sort((a,b) => new Date(a.startTime) - new Date(b.startTime))
        );
      } catch (err) {
        console.error(err);
      }
    };
    fetchTasks();
  }, []);

  // Countdowns
  useEffect(() => {
    if (!upcoming.length) return;
    const updateCounts = () => {
      const now = Date.now();
      const cnts = {};
      upcoming.forEach(t => {
        const diff = new Date(t.startTime).getTime() - now;
        const d = String(Math.floor(diff/86400000)).padStart(2,'0');
        const h = String(Math.floor((diff%86400000)/3600000)).padStart(2,'0');
        const m = String(Math.floor((diff%3600000)/60000)).padStart(2,'0');
        const s = String(Math.floor((diff%60000)/1000)).padStart(2,'0');
        cnts[t._id] = diff>0 ? `${d}:${h}:${m}:${s}` : '00:00:00:00';
      });
      setCountdowns(cnts);
    };
    updateCounts();
    timerRef.current = setInterval(updateCounts, 1000);
    return () => clearInterval(timerRef.current);
  }, [upcoming]);

  const handleCreate = async () => {
    if (!newTitle || !newDateTime) return;
    const token = localStorage.getItem('token');
    try {
      await axios.post('/api/tasks', {
        title: newTitle,
        startTime: newDateTime,
        endTime: newDateTime
      }, {
        headers: { 'x-auth-token': token }
      });
      setNewTitle(''); setNewDateTime(''); setShowCreateModal(false);
      // refrescar…
      const { data } = await axios.get('/api/tasks', {
        headers: { 'x-auth-token': token }
      });
      setTasks(data);
    } catch (err) {
      console.error(err);
    }
  };

  const openManage = () => {
    const init = {};
    tasks.forEach(t => {
      init[t._id] = { title: t.title, datetime: t.startTime.slice(0,16) };
    });
    setEditStates(init);
    setShowManage(true);
  };
  const handleEdit = async id => {
    const token = localStorage.getItem('token');
    const { title, datetime } = editStates[id];
    try {
      await axios.put(`/api/tasks/${id}`, {
        title,
        startTime: datetime,
        endTime: datetime
      }, {
        headers: { 'x-auth-token': token }
      });
      setShowManage(false);
    } catch (err) {
      console.error(err);
    }
  };
  const handleDelete = async id => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`/api/tasks/${id}`, {
        headers: { 'x-auth-token': token }
      });
      setTasks(ts => ts.filter(t => t._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const tileContent = ({ date, view }) => {
    if (view !== 'month') return null;
    const day = date.toISOString().slice(0,10);
    const has = upcoming.some(t=>t.startTime.slice(0,10)===day) ||
                expiredReminders.some(t=>t.startTime.slice(0,10)===day);
    return has ? <div className="task-dot" /> : null;
  };

  const handleLogout = () => navigate('/');

  return (
    <div className="home-container">
      <header className="top-bar">
        <div className="left-section">
          <img
            src={profilePhoto || '/default-avatar.png'}
            alt="Perfil"
            className="user-avatar"
            onClick={() => setShowProfileModal(true)}
          />
        </div>
        <div className="center-section">
          <h1 className="title">JANUS</h1>
        </div>
        <div className="right-section">
          <div className="notification-wrapper">
            <i className="fa fa-bell bell-icon" />
            {expiredReminders.length>0 && <span className="notification-dot" />}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <i className="fa fa-sign-out" /><span>Salir</span>
          </button>
        </div>
      </header>

      {/* Modal: cambiar foto */}
      {showProfileModal && (
        <div className="profile-modal-overlay">
          <div className="profile-modal-content">
            <h2>Cambiar Foto de Perfil</h2>
            {profilePhoto && (
              <img src={profilePhoto} className="profile-pic" alt="Preview" />
            )}
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="profile-input"
            />
            <div className="profile-buttons">
              <button onClick={handleUploadPhoto} disabled={!newPhotoFile}>
                Guardar
              </button>
              <button onClick={() => setShowProfileModal(false)}>
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/** notification panel omitted for brevity **/}

      <main className="main-content">
        <section className="intro-section">
          <h2>¡Bienvenido/a a JANUS!</h2>
          <p>Explora o gestiona tus recordatorios:</p>
          <button className="create-task-btn" onClick={()=>setShowCreateModal(true)}>
            <i className="fa fa-plus" /> Añadir recordatorio
          </button>
          <button className="manage-btn" onClick={openManage}>
            <i className="fa fa-cog" /> Gestionar Recordatorios
          </button>
        </section>

        <div className="buttons-and-calendar">
          <div className="buttons-block">
            <button className="option-btn" onClick={()=>navigate('/messaging')}>
              <i className="fa fa-comments icon" /> Mensajería
            </button>
            <button className="option-btn" onClick={()=>navigate('/time-machine-game')}>
              <i className="fa fa-clock-o icon" /> Máquina del Tiempo
            </button>
            <button className="option-btn" onClick={()=>navigate('/mi-planeta')}>
              <i className="fa fa-globe icon" /> Mi Planeta
            </button>
            <button className="option-btn" onClick={()=>navigate('/patient/templates')}>
              <i className="fa fa-folder-open icon" /> Mis Plantillas
            </button>
            <button className="option-btn" onClick={()=>navigate('/diario-de-emociones')}>
              <i className="fa fa-book icon" /> Diario de Emociones
            </button>
          </div>
          <div className="calendar-block">
            <Calendar
              onChange={setSelectedDate}
              value={selectedDate}
              tileContent={tileContent}
            />
          </div>
        </div>
      </main>

      {/* Crear recordatorio modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Nuevo Recordatorio</h2>
            <label>
              Título
              <input
                type="text"
                value={newTitle}
                onChange={e=>setNewTitle(e.target.value)}
              />
            </label>
            <label>
              Fecha y hora
              <input
                type="datetime-local"
                value={newDateTime}
                onChange={e=>setNewDateTime(e.target.value)}
              />
            </label>
            <div className="modal-buttons">
              <button onClick={handleCreate}>Guardar</button>
              <button onClick={()=>setShowCreateModal(false)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {/* Gestionar recordatorios modal */}
      {showManage && (
        <div className="modal-overlay">
          <div className="modal-content manage-content">
            <h2>Gestionar Recordatorios</h2>
            <ul className="manage-list">
              {tasks.map(t => {
                const { title, datetime } = editStates[t._id]||{};
                return (
                  <li key={t._id} className="manage-item">
                    <input
                      value={title||''}
                      onChange={e=>{
                        setEditStates(es=>({
                          ...es,
                          [t._id]: { ...es[t._id], title: e.target.value }
                        }));
                      }}
                    />
                    <input
                      type="datetime-local"
                      value={datetime||''}
                      onChange={e=>{
                        setEditStates(es=>({
                          ...es,
                          [t._id]: { ...es[t._id], datetime: e.target.value }
                        }));
                      }}
                    />
                    <div className="manage-actions">
                      <button onClick={()=>handleEdit(t._id)}>Guardar</button>
                      <button onClick={()=>handleDelete(t._id)}>Eliminar</button>
                    </div>
                  </li>
                );
              })}
            </ul>
            <button onClick={()=>setShowManage(false)}>Cerrar</button>
          </div>
        </div>
      )}

      <footer className="footer-bar">
        <p>2025 © Iván Vela Campos</p>
      </footer>
    </div>
  );
}
