import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import '../styles/timeMachineGame.css';

const generateTimeSlots = () => {
  // Genera franjas de 30 minutos durante las 24 horas (48 slots)
  const slots = [];
  for (let i = 0; i < 48; i++) {
    // Calculamos la hora y minutos
    const hour = Math.floor(i / 2);
    const minutes = i % 2 === 0 ? '00' : '30';
    const label = `${String(hour).padStart(2, '0')}:${minutes}`;
    slots.push({ id: label, activities: [] });
  }
  return slots;
};

const initialActivities = [
  { id: 'futbol', title: 'Jugar al f√∫tbol', icon: '‚öΩ' },
  { id: 'helado', title: 'Comer helado', icon: 'üç¶' },
  { id: 'caricaturas', title: 'Ver caricaturas', icon: 'üì∫' },
  { id: 'bicicleta', title: 'Montar bicicleta', icon: 'üö≤' },
  { id: 'videojuegos', title: 'Jugar videojuegos', icon: 'üéÆ' },
  { id: 'comics', title: 'Leer c√≥mics', icon: 'üìö' },
  { id: 'lego', title: 'Construir con LEGO', icon: 'üß±' },
  { id: 'parque', title: 'Ir al parque', icon: 'üèûÔ∏è' },
  { id: 'ba√±arse', title: 'Ba√±arse', icon: 'üõÅ' },
  { id: 'siesta', title: 'Dormir la siesta', icon: 'üò¥' },
  { id: 'travesuras', title: 'Hacer travesuras', icon: 'üòú' },
  { id: 'correr', title: 'Correr en el parque', icon: 'üèÉ‚Äç‚ôÇÔ∏è' },
  { id: 'pintar', title: 'Pintar dibujos', icon: 'üé®' },
  { id: 'juegosMesa', title: 'Jugar a juegos de mesa', icon: 'üé≤' },
  { id: 'robotica', title: 'Construir robots', icon: 'ü§ñ' },
  { id: 'escuela', title: 'Ir a la escuela (juego)', icon: 'üè´' },
  { id: 'nadar', title: 'Nadar', icon: 'üèä‚Äç‚ôÇÔ∏è' },
  { id: 'cocinar', title: 'Cocinar algo rico', icon: 'üç≥' },
  { id: 'misterios', title: 'Resolver misterios', icon: 'üïµÔ∏è' },
  { id: 'explorar', title: 'Explorar el barrio', icon: 'üó∫Ô∏è' }
];

function TimeMachineGame() {
  const [selectedDay, setSelectedDay] = useState('Mi√©rcoles'); // o "S√°bado"
  const [timeSlots, setTimeSlots] = useState(generateTimeSlots());
  const [activityPool, setActivityPool] = useState(initialActivities);
  const [instructionsVisible, setInstructionsVisible] = useState(true);
  const [saving, setSaving] = useState(false);

  // Manejo del drag & drop
  const onDragEnd = (result) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    // Caso 1: Desde el pool hacia un slot
    if (source.droppableId === 'pool' && destination.droppableId !== 'pool') {
      const activity = activityPool[source.index];
      // Creamos una instancia nueva (sin quitarla del pool, para permitir reuso)
      const instance = {
        instanceId: `${activity.id}-${new Date().getTime()}`,
        templateId: activity.id,
        title: activity.title,
        icon: activity.icon,
        duration: 30 // duraci√≥n por defecto (30 min)
      };
      const newSlots = Array.from(timeSlots);
      const destIndex = newSlots.findIndex(slot => slot.id === destination.droppableId);
      const updatedActivities = Array.from(newSlots[destIndex].activities);
      updatedActivities.splice(destination.index, 0, instance);
      newSlots[destIndex].activities = updatedActivities;
      setTimeSlots(newSlots);
      return;
    }

    // Caso 2: Mover dentro de la misma columna o entre slots
    if (source.droppableId !== 'pool' && destination.droppableId !== 'pool') {
      const newSlots = Array.from(timeSlots);
      const sourceSlotIndex = newSlots.findIndex(slot => slot.id === source.droppableId);
      const destSlotIndex = newSlots.findIndex(slot => slot.id === destination.droppableId);
      const sourceActivities = Array.from(newSlots[sourceSlotIndex].activities);
      const [movedItem] = sourceActivities.splice(source.index, 1);
      newSlots[sourceSlotIndex].activities = sourceActivities;
      const destActivities = Array.from(newSlots[destSlotIndex].activities);
      destActivities.splice(destination.index, 0, movedItem);
      newSlots[destSlotIndex].activities = destActivities;
      setTimeSlots(newSlots);
      return;
    }
  };

  // Actualizar la duraci√≥n de una actividad mediante slider
  const handleDurationChange = (slotId, index, newDuration) => {
    const newSlots = Array.from(timeSlots);
    const slotIndex = newSlots.findIndex(slot => slot.id === slotId);
    newSlots[slotIndex].activities[index].duration = newDuration;
    setTimeSlots(newSlots);
  };

  // Guardar el resultado del juego (llamada a la API)
  const handleSaveResult = async () => {
    setSaving(true);
    const resultData = {
      day: selectedDay,
      timeSlots: timeSlots.filter(slot => slot.activities.length > 0)
    };
    try {
      await axios.post('/api/time-machine-game', resultData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      alert('¬°Tu d√≠a ha sido guardado!');
    } catch (error) {
      console.error('Error guardando el resultado:', error);
      alert('Error al guardar. Int√©ntalo de nuevo.');
    }
    setSaving(false);
  };

  // Cambiar entre mi√©rcoles y s√°bado
  const toggleDay = () => {
    setSelectedDay(prev => (prev === 'Mi√©rcoles' ? 'S√°bado' : 'Mi√©rcoles'));
    // Reiniciar el tablero si se desea, por ejemplo:
    setTimeSlots(generateTimeSlots());
  };

  return (
    <div className="tm-game-container">
      <header className="tm-game-header">
        <h1>La M√°quina del Tiempo</h1>
        <button className="toggle-day-btn" onClick={toggleDay}>
          {selectedDay === 'Mi√©rcoles' ? 'Cambiar a S√°bado' : 'Cambiar a Mi√©rcoles'}
        </button>
      </header>

      {instructionsVisible && (
        <div className="tm-game-instructions">
          <p>
            ¬°Esta es tu m√°quina del tiempo! Arrastra las actividades que normalmente haces un {selectedDay}. 
            Ajusta el tiempo deslizando la barra para cada actividad.
          </p>
          <button onClick={() => setInstructionsVisible(false)}>Cerrar instrucciones</button>
        </div>
      )}

      <div className="tm-game-content">
        <aside className="tm-game-pool">
          <h2>Actividades divertidas</h2>
          <Droppable droppableId="pool">
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps}>
                {activityPool.map((activity, index) => (
                  <Draggable key={activity.id} draggableId={activity.id} index={index}>
                    {(providedDraggable) => (
                      <div 
                        className="tm-game-card"
                        ref={providedDraggable.innerRef}
                        {...providedDraggable.draggableProps}
                        {...providedDraggable.dragHandleProps}
                      >
                        <span className="tm-game-card-icon">{activity.icon}</span>
                        <span className="tm-game-card-title">{activity.title}</span>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </aside>

        <main className="tm-game-board">
          <DragDropContext onDragEnd={onDragEnd}>
            {timeSlots.map((slot) => (
              <Droppable key={slot.id} droppableId={slot.id}>
                {(provided) => (
                  <div 
                    className="tm-game-slot"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    <div className="tm-game-slot-label">{slot.id}</div>
                    <div className="tm-game-slot-activities">
                      {slot.activities.map((activity, index) => (
                        <Draggable 
                          key={activity.instanceId} 
                          draggableId={activity.instanceId} 
                          index={index}
                        >
                          {(providedDraggable) => (
                            <div 
                              className="tm-game-activity-card"
                              ref={providedDraggable.innerRef}
                              {...providedDraggable.draggableProps}
                              {...providedDraggable.dragHandleProps}
                            >
                              <div className="activity-header">
                                <span className="activity-icon">{activity.icon}</span>
                                <span className="activity-title">{activity.title}</span>
                              </div>
                              <div className="activity-duration">
                                <input 
                                  type="range"
                                  min="15"
                                  max="120"
                                  step="15"
                                  value={activity.duration}
                                  onChange={(e) =>
                                    handleDurationChange(slot.id, index, parseInt(e.target.value, 10))
                                  }
                                />
                                <span>{activity.duration} min</span>
                              </div>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  </div>
                )}
              </Droppable>
            ))}
          </DragDropContext>
        </main>
      </div>

      <footer className="tm-game-footer">
        <button className="save-result-btn" onClick={handleSaveResult} disabled={saving}>
          {saving ? 'Guardando...' : 'Guardar mi d√≠a'}
        </button>
      </footer>
    </div>
  );
}

export default TimeMachineGame;
