// src/components/TemplateForm.js
import React, { useState } from 'react';
import { useNavigate }       from 'react-router-dom';
import axios                 from 'axios';
import '../styles/routineTemplates.css';

export default function TemplateForm() {
  const navigate = useNavigate();
  const [name, setName]           = useState('');
  const [description, setDesc]    = useState('');
  const [activities, setActs]     = useState([]);
  const [tempAct, setTempAct]     = useState({ title:'', icon:'fa fa-star', challenge:'leve' });

  const addActivity = () => {
    setActs([...activities, tempAct]);
    setTempAct({ title:'', icon:'fa fa-star', challenge:'leve' });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    await axios.post('/api/routines/templates',
      { name, description, activities },
      { headers:{ 'x-auth-token':token } }
    );
    navigate('/therapist/templates');
  };

  return (
    <form className="tpl-form" onSubmit={handleSubmit}>
      <h2>Crear Nueva Plantilla</h2>
      <label>Nombre:</label>
      <input value={name} onChange={e=>setName(e.target.value)} required />

      <label>Descripci√≥n:</label>
      <textarea value={description} onChange={e=>setDesc(e.target.value)} required />

      <h3>Actividades</h3>
      <div className="activity-builder">
        <input
          placeholder="T√≠tulo"
          value={tempAct.title}
          onChange={e=>setTempAct({...tempAct, title:e.target.value})}
          required
        />
        <select
          value={tempAct.icon}
          onChange={e=>setTempAct({...tempAct, icon:e.target.value})}
        >
          <option value="fa fa-running">üèÉ‚Äç‚ôÇÔ∏è Running</option>
          <option value="fa fa-book">üìö Lectura</option>
          <option value="fa fa-music">üéµ M√∫sica</option>
          <option value="fa fa-paint-brush">üé® Arte</option>
        </select>
        <select
          value={tempAct.challenge}
          onChange={e=>setTempAct({...tempAct, challenge:e.target.value})}
        >
          <option value="leve">Desaf√≠o Leve</option>
          <option value="moderado">Moderado</option>
          <option value="intenso">Intenso</option>
        </select>
        <button type="button" onClick={addActivity}>Agregar</button>
      </div>

      <ul className="activity-preview">
        {activities.map((a,i)=>(
          <li key={i}>
            <i className={`${a.icon} fa-2x`}></i>
            {a.title} ‚Äî <strong>{a.challenge}</strong>
          </li>
        ))}
      </ul>

      <button type="submit" className="btn-save">Guardar Plantilla</button>
    </form>
  );
}
